import React, { useRef, useEffect, useState, useMemo } from 'react';
import * as d3 from 'd3';

// Helper: Darken a HEX color by a given factor.
const darkenColor = (hex, factor = 0.8) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.floor(c.r * factor);
    c.g = Math.floor(c.g * factor);
    c.b = Math.floor(c.b * factor);
    return c.formatHex();
  }
  return hex;
};

// Helper: Lighten a HEX color by a given factor.
const lightenColor = (hex, factor = 1.2) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.min(255, Math.floor(c.r * factor));
    c.g = Math.min(255, Math.floor(c.g * factor));
    c.b = Math.min(255, Math.floor(c.b * factor));
    return c.formatHex();
  }
  return hex;
};

// Auto-detect the production date column from common names.
const detectDateColumn = (headers) => {
  const candidates = headers.filter(h => {
    const lower = h.toLowerCase();
    return (lower.includes("prod") && lower.includes("date")) ||
           lower.includes("proddt") ||
           lower.includes("proddttm") ||
           lower === "date" ||
           lower === "datetime";
  });
  return candidates.length > 0 ? candidates[0] : "Production_Date";
};

// Auto-detect measurement columns using keywords.
const detectColumns = (headers) => {
  let phases = {};
  headers.forEach(header => {
    const lower = header.toLowerCase();
    // For oil: prefer "bopd", otherwise any "oil".
    if (lower.includes("bopd")) {
      phases.oil = header;
    } else if (lower.includes("oil") && !phases.oil) {
      phases.oil = header;
    }
    // For water: prefer "bwpd", otherwise any "water".
    if (lower.includes("bwpd")) {
      phases.water = header;
    } else if (lower.includes("water") && !phases.water) {
      phases.water = header;
    }
    // For gas: prefer "mcfd", otherwise any "gas".
    if (lower.includes("mcfd")) {
      phases.gas = header;
    } else if (lower.includes("gas") && !phases.gas) {
      phases.gas = header;
    }
    // For pressure: look for "pip", "pressure", or "psi".
    if ((lower.includes("pip") || lower.includes("pressure") || lower.includes("psi")) && !phases.pressure) {
      phases.pressure = header;
    }
  });
  return phases;
};

// Compute the average production over the first week (t <= 7 days)
// Fallback: if no week data exists, use the first data point.
const computeAverageFirstWeek = (series) => {
  const weekData = series.filter(d => d.t <= 7);
  return weekData.length > 0 ? d3.mean(weekData, d => d.value) : (series[0] ? series[0].value : 0);
};

// Brute-force search for best-fit D using the last 60 days.
const fitD = (fitData, Qi, b) => {
  let bestD = 0.01;
  let bestError = Infinity;
  for (let D = 0.001; D <= 0.1; D += 0.001) {
    let error = 0;
    fitData.forEach(d => {
      const predicted = Qi / Math.pow(1 + b * D * d.t, 1 / b);
      error += Math.pow(predicted - d.value, 2);
    });
    if (error < bestError) {
      bestError = error;
      bestD = D;
    }
  }
  return bestD;
};

const InteractiveDCAChart = ({ dataString, startDate, endDate }) => {
  // Parse CSV data.
  const parsedData = useMemo(() => {
    try {
      return d3.csvParse(dataString);
    } catch (err) {
      console.error("Error parsing CSV:", err);
      return [];
    }
  }, [dataString]);

  const hasData = parsedData && parsedData.length > 0;
  const svgRef = useRef(null);
  const prodDateCol = hasData ? detectDateColumn(parsedData.columns) : "Production_Date";

  // Filter data by date.
  const filteredData = useMemo(() => {
    if (!hasData) return [];
    let dataWithDates = parsedData.map(row => ({
      ...row,
      [prodDateCol]: new Date(row[prodDateCol])
    }));
    dataWithDates = dataWithDates.filter(row => {
      const d = new Date(row[prodDateCol]);
      return !isNaN(d.getTime());
    });
    if (startDate) {
      const sd = new Date(startDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] >= sd);
    }
    if (endDate) {
      const ed = new Date(endDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] <= ed);
    }
    return dataWithDates;
  }, [parsedData, startDate, endDate, hasData, prodDateCol]);

  const localHasData = filteredData && filteredData.length > 0;
  const firstDate = localHasData ? new Date(filteredData[0][prodDateCol]) : new Date();
  const headers = parsedData.columns ? parsedData.columns : [];
  const phasesDetected = detectColumns(headers);

  const data = localHasData
    ? filteredData.map(row => {
        const date = new Date(row[prodDateCol]);
        return { ...row, Production_Date: date, t: (date - firstDate) / (1000 * 60 * 60 * 24) };
      })
    : [];

  const phaseData = {};
  Object.keys(phasesDetected).forEach(phase => {
    phaseData[phase] = localHasData
      ? data.map(d => ({ t: d.t, value: +d[phasesDetected[phase]] }))
      : [];
  });

  const tMax = localHasData ? d3.max(data, d => d.t) : 0;
  const maxProdDate = localHasData ? d3.max(data, d => d.Production_Date) : firstDate;
  // For forecasting, instead of computing forecast start from data,
  // we start the forecast at today.
  const forecastStartDate = new Date();
  const tForecastStart = 0; // Forecast starts at t = 0 relative to forecastStartDate

  // For historical curves, use initial Qi from the first data point.
  const initialParams = {};
  if (localHasData) {
    Object.keys(phaseData).forEach(phase => {
      const series = phaseData[phase];
      const Qi = series[0] ? series[0].value : 0;
      const b = 0.9;
      const fitSeries = series.filter(d => d.t >= (tMax >= 60 ? tMax - 60 : 0));
      const D = fitSeries.length > 0 ? fitD(fitSeries, Qi, b) : 0.01;
      initialParams[phase] = { Qi, b, D };
    });
  }

  const defaultColors = { oil: "#008000", water: "#0000ff", gas: "#ff0000", pressure: "#000000" };

  const [phaseParams, setPhaseParams] = useState(initialParams);
  const [colors, setColors] = useState(defaultColors);
  const [activeKey, setActiveKey] = useState(null);
  const [yScaleType, setYScaleType] = useState("linear");
  const [yMultiplier, setYMultiplier] = useState(1.1);
  const [forecastDays, setForecastDays] = useState(30);

  useEffect(() => {
    const handleKeyDown = (e) => {
      const key = e.key.toLowerCase();
      if (["d", "q", "b"].includes(key)) setActiveKey(key);
    };
    const handleKeyUp = () => setActiveKey(null);
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  useEffect(() => {
    if (!localHasData) return;
    const margin = { top: 20, right: 30, bottom: 40, left: 60 };
    const width = 800;
    const height = 400;
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    const xScale = d3.scaleTime()
      .domain(d3.extent(data, d => d.Production_Date))
      .range([margin.left, width - margin.right]);

    const maxYValue = d3.max(Object.keys(phaseData), phase =>
      d3.max(phaseData[phase], d => d.value)
    );
    let yScale;
    if (yScaleType === "log") {
      yScale = d3.scaleLog().domain([1, maxYValue * yMultiplier]).range([height - margin.bottom, margin.top]);
    } else {
      yScale = d3.scaleLinear().domain([0, maxYValue * yMultiplier]).range([height - margin.bottom, margin.top]);
    }

    svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(d3.axisBottom(xScale).tickFormat(d3.timeFormat("%Y-%m-%d")))
      .selectAll("text")
      .attr("transform", "rotate(-45)")
      .style("text-anchor", "end");

    svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yScaleType === "log" ? d3.axisLeft(yScale).ticks(5, "~s") : d3.axisLeft(yScale));

    Object.keys(phaseData).forEach(phase => {
      svg.selectAll(`circle.${phase}`)
        .data(phaseData[phase])
        .enter()
        .append("circle")
        .attr("class", phase)
        .attr("cx", d => xScale(new Date(firstDate.getTime() + d.t * 24 * 60 * 60 * 1000)))
        .attr("cy", d => yScale(d.value))
        .attr("r", 3)
        .attr("fill", colors[phase] || "#888888");
    });

    // For each phase, draw the historical decline curve based on the data.
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      // Compute historical decline from t=0 to tMax.
      const histData = [];
      const numPoints = 100;
      for (let i = 0; i <= numPoints; i++) {
        const t = (tMax * i) / numPoints;
        const Q = (b === 0)
          ? Qi * Math.exp(-D * t)
          : Qi / Math.pow(1 + b * D * t, 1 / b);
        const date = new Date(firstDate.getTime() + t * 24 * 60 * 60 * 1000);
        histData.push({ t, Q, Production_Date: date });
      }
      const lineGenHist = d3.line()
        .x(d => xScale(d.Production_Date))
        .y(d => yScale(d.Q));
      svg.append("path")
        .datum(histData)
        .attr("fill", "none")
        .attr("stroke", colors[phase] || "#888888")
        .attr("stroke-width", 2)
        .attr("d", lineGenHist)
        .style("cursor", "pointer")
        .call(d3.drag()
          .on("drag", function(event) {
            let newParams = { ...phaseParams[phase] };
            if (activeKey === "d") {
              newParams.D = Math.max(0.0001, newParams.D + event.dy * 0.005);
            } else if (activeKey === "q") {
              newParams.Qi = Math.max(1, newParams.Qi - event.dy * 0.5);
            } else if (activeKey === "b") {
              newParams.b = Math.max(0.01, newParams.b + event.dx * 0.005);
            }
            setPhaseParams(prev => ({ ...prev, [phase]: newParams }));
          })
        );
      
      // For testing: Draw a forecast curve that always starts at today's date
      // with an initial value of 100, using the same b and D.
      const forecastData = [];
      const numForecastPoints = 100;
      const forecastStartDate = new Date(); // forecast starts today
      for (let i = 0; i <= numForecastPoints; i++) {
        const t = (forecastDays * i) / numForecastPoints;
        const Q = (b === 0)
          ? 100 * Math.exp(-D * t)
          : 100 / Math.pow(1 + b * D * t, 1 / b);
        const date = new Date(forecastStartDate.getTime() + t * 24 * 60 * 60 * 1000);
        forecastData.push({ t, Q, Production_Date: date });
      }
      const lineGenForecast = d3.line()
        .x(d => xScale(d.Production_Date))
        .y(d => yScale(d.Q));
      svg.append("path")
        .datum(forecastData)
        .attr("fill", "none")
        .attr("stroke", lightenColor(colors[phase] || "#888888", 1.2))
        .attr("stroke-width", 3)
        .attr("d", lineGenForecast);
    });
  }, [data, firstDate, tMax, phaseData, phaseParams, colors, activeKey, localHasData, yMultiplier, yScaleType, forecastDays]);

  return (
    <div>
      <h3>Interactive Decline Curve Forecast</h3>
      <svg ref={svgRef} width={800} height={400}></svg>
      <div style={{ marginTop: '10px' }}>
        {Object.keys(phaseParams).map(phase => (
          <div key={phase}>
            <strong>{phase.toUpperCase()}</strong>: Qi = {phaseParams[phase].Qi.toFixed(2)}, b = {phaseParams[phase].b.toFixed(3)}, D = {phaseParams[phase].D.toFixed(4)}
          </div>
        ))}
        <div style={{ marginTop: '10px' }}>
          <p>
            <strong>Drag Instructions:</strong><br />
            Hold <strong>D</strong> to adjust D,<br />
            Hold <strong>Q</strong> to adjust Qi,<br />
            Hold <strong>B</strong> to adjust b.<br />
            (Forecast curve starts at today with an initial value of 100.)
          </p>
        </div>
        <div style={{ marginTop: '10px' }}>
          <h4>Axis Controls:</h4>
          <div>
            <label>
              Y-Axis Scale Type:&nbsp;
              <label>
                <input
                  type="radio"
                  name="yScaleType"
                  value="linear"
                  checked={yScaleType === "linear"}
                  onChange={() => setYScaleType("linear")}
                /> Linear
              </label>
              &nbsp;
              <label>
                <input
                  type="radio"
                  name="yScaleType"
                  value="log"
                  checked={yScaleType === "log"}
                  onChange={() => setYScaleType("log")}
                /> Log
              </label>
            </label>
          </div>
          <div>
            <label>
              Y-Axis Multiplier: {yMultiplier.toFixed(2)}
              <input
                type="range"
                min="1"
                max="3"
                step="0.05"
                value={yMultiplier}
                onChange={(e) => setYMultiplier(+e.target.value)}
              />
            </label>
          </div>
          <div>
            <label>
              Forecast Extension (Days): {forecastDays}
              <input
                type="range"
                min="0"
                max="90"
                step="1"
                value={forecastDays}
                onChange={(e) => setForecastDays(+e.target.value)}
              />
            </label>
          </div>
        </div>
        <div style={{ marginTop: '10px' }}>
          <h4>Change Colors:</h4>
          {Object.keys(colors).map(phase => (
            <div key={phase} style={{ marginBottom: '5px' }}>
              <label>
                {phase.toUpperCase()} Color:&nbsp;
                <input
                  type="color"
                  value={colors[phase]}
                  onChange={(e) => setColors(prev => ({ ...prev, [phase]: e.target.value }))}
                />
              </label>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default InteractiveDCAChart;
