import React, { useState, useEffect } from 'react';
import queryString from 'query-string';
import * as XLSX from 'xlsx';
import * as d3 from 'd3';
import InteractiveDCAChart from './InteractiveDCAChart';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

// Helper: Auto-detect production date column.
const detectDateColumn = (headers) => {
  const candidates = headers.filter(h => {
    const lower = h.toLowerCase();
    return (lower.includes("prod") && lower.includes("date")) ||
           lower.includes("proddt") ||
           lower.includes("proddttm") ||
           lower === "date" ||
           lower === "datetime";
  });
  return candidates.length > 0 ? candidates[0] : "Production_Date";
};

function App() {
  const [secret, setSecret] = useState('');
  const [dataString, setDataString] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  // Date filter states.
  const [minDate, setMinDate] = useState(null);
  const [maxDate, setMaxDate] = useState(null);
  // Use a range (two values) for the slider
  const [dateRange, setDateRange] = useState([0, 0]);

  useEffect(() => {
    const params = queryString.parse(window.location.search);
    if (params.secret) setSecret(params.secret);
    if (params.data) setDataString(params.data);
  }, []);

  // When dataString changes, parse it to determine min and max dates.
  useEffect(() => {
    if (!dataString) return;
    try {
      const parsed = d3.csvParse(dataString);
      if (parsed && parsed.length > 0 && parsed.columns) {
        const dateCol = detectDateColumn(parsed.columns);
        const dates = parsed.map(row => new Date(row[dateCol])).filter(d => !isNaN(d));
        if (dates.length > 0) {
          const minD = new Date(Math.min(...dates));
          const maxD = new Date(Math.max(...dates));
          setMinDate(minD);
          setMaxDate(maxD);
          const totalDays = Math.ceil((maxD.getTime() - minD.getTime()) / (1000 * 60 * 60 * 24));
          setDateRange([0, totalDays]);
        }
      }
    } catch (e) {
      console.error("Error parsing dates", e);
    }
  }, [dataString]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    const fileName = file.name.toLowerCase();
    const reader = new FileReader();
    if (fileName.endsWith('.csv') || fileName.endsWith('.txt')) {
      reader.onload = (e) => setDataString(e.target.result);
      reader.readAsText(file);
    } else if (fileName.endsWith('.xls') || fileName.endsWith('.xlsx')) {
      reader.onload = (e) => {
        const data = new Uint8Array(e.target.result);
        const workbook = XLSX.read(data, { type: 'array' });
        const firstSheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[firstSheetName];
        const csv = XLSX.utils.sheet_to_csv(worksheet);
        setDataString(csv);
      };
      reader.readAsArrayBuffer(file);
    } else {
      alert('Unsupported file format. Please upload CSV, TXT, XLS, or XLSX.');
    }
  };

  const handleSubmit = async () => {
    if (!secret || !dataString) {
      setError('Secret key and data are required.');
      return;
    }
    try {
      const res = await fetch('http://127.0.0.1:5000/api/validate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ secret, data: dataString })
      });
      const json = await res.json();
      if (json.error) {
        setError(json.error);
        setResponse(null);
      } else {
        setResponse(json);
        setError(null);
      }
    } catch (err) {
      setError('Error connecting to backend: ' + err.toString());
      setResponse(null);
    }
  };

  const totalDays = minDate && maxDate 
    ? Math.ceil((maxDate.getTime() - minDate.getTime()) / (1000 * 60 * 60 * 24))
    : 0;

  // Calculate filtered start and end dates based on the dual-range slider.
  const filteredStartDate = minDate 
    ? new Date(minDate.getTime() + dateRange[0] * 24 * 60 * 60 * 1000)
    : null;
  const filteredEndDate = minDate 
    ? new Date(minDate.getTime() + dateRange[1] * 24 * 60 * 60 * 1000)
    : null;

  return (
    <div style={{ padding: 20, fontFamily: 'Arial, sans-serif' }}>
      <h2>Custom DCA Application</h2>
      <div>
        <label>
          Secret Key:&nbsp;
          <input
            type="text"
            value={secret}
            onChange={(e) => setSecret(e.target.value)}
            placeholder="Enter secret key"
            style={{ width: '300px' }}
          />
        </label>
      </div>
      <div style={{ marginTop: 10 }}>
        <label>
          Upload CSV/Excel file:&nbsp;
          <input type="file" onChange={handleFileUpload} />
        </label>
      </div>
      <div style={{ marginTop: 10 }}>
        <textarea
          value={dataString}
          onChange={(e) => setDataString(e.target.value)}
          placeholder="CSV data will appear here, or paste CSV data directly..."
          rows="6"
          cols="80"
        ></textarea>
      </div>
      {minDate && maxDate && (
        <div style={{ marginTop: 10 }}>
          <label>
            Production Date Range (offset from {minDate.toDateString()}):
            <Slider.Range
              min={0}
              max={totalDays}
              value={dateRange}
              onChange={(newRange) => setDateRange(newRange)}
              tipFormatter={value => {
                const d = new Date(minDate.getTime() + value * 24 * 60 * 60 * 1000);
                return d.toDateString();
              }}
            />
            <div>
              {filteredStartDate.toDateString()} â€“ {filteredEndDate.toDateString()}
            </div>
          </label>
        </div>
      )}
      <div style={{ marginTop: 10 }}>
        <button onClick={handleSubmit}>Submit Data</button>
      </div>
      {error && <div style={{ color: 'red', marginTop: 10 }}>{error}</div>}
      {response && (
        <div style={{ marginTop: 10 }}>
          <h3>Data Successfully Processed</h3>
          <p>Columns: {response.columns.join(', ')}</p>
          <p>Rows: {response.rows}</p>
        </div>
      )}
      {dataString && (
        <InteractiveDCAChart
          dataString={dataString}
          startDate={filteredStartDate ? filteredStartDate.toISOString().slice(0,10) : ""}
          endDate={filteredEndDate ? filteredEndDate.toISOString().slice(0,10) : ""}
        />
      )}
    </div>
  );
}

export default App;
