import React, { useRef, useEffect, useState, useMemo } from 'react';
import * as d3 from 'd3';

const darkenColor = (hex, factor = 0.8) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.floor(c.r * factor);
    c.g = Math.floor(c.g * factor);
    c.b = Math.floor(c.b * factor);
    return c.formatHex();
  }
  return hex;
};

const lightenColor = (hex, factor = 1.2) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.min(255, Math.floor(c.r * factor));
    c.g = Math.min(255, Math.floor(c.g * factor));
    c.b = Math.min(255, Math.floor(c.b * factor));
    return c.formatHex();
  }
  return hex;
};

const detectDateColumn = (headers) => {
  const candidates = headers.filter(h => {
    const lower = h.toLowerCase();
    return (lower.includes("prod") && lower.includes("date")) ||
           lower.includes("proddt") ||
           lower.includes("proddttm") ||
           lower === "date" ||
           lower === "datetime";
  });
  return candidates.length > 0 ? candidates[0] : "Production_Date";
};

const detectColumns = (headers) => {
  let phases = {};
  headers.forEach(header => {
    const lower = header.toLowerCase();
    if (lower.includes("bopd")) {
      phases.oil = header;
    } else if (lower.includes("oil") && !phases.oil) {
      phases.oil = header;
    }
    if (lower.includes("bwpd")) {
      phases.water = header;
    } else if (lower.includes("water") && !phases.water) {
      phases.water = header;
    }
    if (lower.includes("mcfd")) {
      phases.gas = header;
    } else if (lower.includes("gas") && !phases.gas) {
      phases.gas = header;
    }
    if ((lower.includes("pip") || lower.includes("pressure") || lower.includes("psi")) && !phases.pressure) {
      phases.pressure = header;
    }
  });
  return phases;
};

const calculateRate = (Qi, b, D, t) => {
  if (b === 0) {
    return Qi * Math.exp(-D * t);
  } else {
    return Qi / Math.pow(1 + b * D * t, 1 / b);
  }
};

const fitDeclineParameters = (data, fitWindow = 60) => {
  if (!data || data.length < 3) return { Qi: data?.[0]?.value || 100, b: 0.9, D: 0.01 };
  const sortedData = [...data].sort((a, b) => a.t - b.t);
  const Qi = sortedData[0].value;
  const recentData = sortedData.slice(-Math.min(fitWindow, sortedData.length));
  let bestB = 0;
  let bestD = 0.01;
  let bestError = Infinity;
  for (let b = 0; b <= 1; b += 0.1) {
    const { D, error } = findOptimalD(recentData, Qi, b);
    if (error < bestError) {
      bestError = error;
      bestB = b;
      bestD = D;
    }
  }
  const minB = Math.max(0, bestB - 0.1);
  const maxB = Math.min(1, bestB + 0.1);
  for (let b = minB; b <= maxB; b += 0.01) {
    const { D, error } = findOptimalD(recentData, Qi, b);
    if (error < bestError) {
      bestError = error;
      bestB = b;
      bestD = D;
    }
  }
  return { Qi, b: bestB, D: bestD };
};

const findOptimalD = (data, Qi, b) => {
  let bestD = 0.01;
  let bestError = Infinity;
  for (let D = 0.001; D <= 0.5; D += 0.005) {
    let error = 0;
    data.forEach(d => {
      const predicted = calculateRate(Qi, b, D, d.t);
      error += Math.pow(predicted - d.value, 2);
    });
    if (error < bestError) {
      bestError = error;
      bestD = D;
    }
  }
  const minD = Math.max(0.001, bestD - 0.005);
  const maxD = bestD + 0.005;
  for (let D = minD; D <= maxD; D += 0.0005) {
    let error = 0;
    data.forEach(d => {
      const predicted = calculateRate(Qi, b, D, d.t);
      error += Math.pow(predicted - d.value, 2);
    });
    if (error < bestError) {
      bestError = error;
      bestD = D;
    }
  }
  return { D: bestD, error: bestError };
};

const InteractiveDCAChart = ({
  dataString,
  startDate,
  endDate,
  colors,
  yScaleType,
  yMultiplier,
  forecastDays,
  autoForecast = true,
  onParametersCalculated
}) => {
  const svgRef = useRef(null);

  // Parse CSV data.
  const parsedData = useMemo(() => {
    try {
      return d3.csvParse(dataString);
    } catch (err) {
      console.error("Error parsing CSV:", err);
      return [];
    }
  }, [dataString]);

  const hasData = parsedData && parsedData.length > 0;
  const prodDateCol = hasData ? detectDateColumn(parsedData.columns) : "Production_Date";
  const phasesDetected = hasData ? detectColumns(parsedData.columns) : {};

  const filteredData = useMemo(() => {
    if (!hasData) return [];
    let dataWithDates = parsedData.map(row => ({
      ...row,
      [prodDateCol]: new Date(row[prodDateCol])
    }));
    dataWithDates = dataWithDates.filter(row => {
      const d = new Date(row[prodDateCol]);
      return !isNaN(d.getTime());
    });
    dataWithDates.sort((a, b) => a[prodDateCol] - b[prodDateCol]);
    if (startDate) {
      const sd = new Date(startDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] >= sd);
    }
    if (endDate) {
      const ed = new Date(endDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] <= ed);
    }
    return dataWithDates;
  }, [parsedData, startDate, endDate, hasData, prodDateCol]);

  const localHasData = filteredData && filteredData.length > 0;
  const firstDate = localHasData ? filteredData[0][prodDateCol] : new Date();

  const data = useMemo(() => {
    if (!localHasData) return [];
    return filteredData.map(row => {
      const date = row[prodDateCol];
      return {
        ...row,
        Production_Date: date,
        t: (date - firstDate) / (1000 * 60 * 60 * 24)
      };
    });
  }, [filteredData, prodDateCol, localHasData, firstDate]);

  const phaseData = useMemo(() => {
    const result = {};
    Object.keys(phasesDetected).forEach(phase => {
      result[phase] = localHasData
        ? data
            .map(d => ({
              t: d.t,
              value: +d[phasesDetected[phase]]
            }))
            .filter(d => !isNaN(d.value))
        : [];
    });
    return result;
  }, [data, phasesDetected, localHasData]);

  const tMax = localHasData ? d3.max(data, d => d.t) : 0;
  const maxProdDate = localHasData
    ? d3.max(data, d => d.Production_Date)
    : new Date();

  const [debouncedPhaseData, setDebouncedPhaseData] = useState(phaseData);
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedPhaseData(phaseData);
    }, 500);
    return () => clearTimeout(handler);
  }, [phaseData]);

  const initialParams = useMemo(() => {
    const params = {};
    if (localHasData) {
      Object.keys(debouncedPhaseData).forEach(phase => {
        const series = debouncedPhaseData[phase];
        if (!series || series.length < 3) {
          params[phase] = { Qi: series?.[0]?.value || 100, b: 0.9, D: 0.01 };
          return;
        }
        params[phase] = fitDeclineParameters(series);
      });
    }
    return params;
  }, [debouncedPhaseData, localHasData]);

  const [phaseParams, setPhaseParams] = useState(initialParams);
  const [autoFitEnabled, setAutoFitEnabled] = useState(true);
  const [activeKey, setActiveKey] = useState(null);

  useEffect(() => {
    if (autoForecast && autoFitEnabled) {
      setPhaseParams(initialParams);
    }
  }, [initialParams, autoForecast, autoFitEnabled]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      const key = e.key.toLowerCase();
      if (["d", "q", "b"].includes(key)) {
        setActiveKey(key);
      }
    };
    const handleKeyUp = () => setActiveKey(null);
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  const allHistoricalPoints = useMemo(() => {
    const points = [];
    Object.keys(phaseData).forEach(phase => {
      phaseData[phase].forEach(d => {
        points.push({
          date: new Date(firstDate.getTime() + d.t * 24 * 60 * 60 * 1000),
          Q: d.value,
          phase
        });
      });
    });
    return points;
  }, [phaseData, firstDate]);

  const allForecastPoints = useMemo(() => {
    const forecasts = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      if (!Qi || D <= 0) {
        forecasts[phase] = [];
        return;
      }
      const Q_start = calculateRate(Qi, b, D, tMax);
      const forecastStartDate = maxProdDate;
      const forecastData = [];
      const numForecastPts = 50;
      for (let i = 0; i <= numForecastPts; i++) {
        const tForecast = (forecastDays * i) / numForecastPts;
        const Q_forecast = calculateRate(Q_start, b, D, tForecast);
        const date = new Date(forecastStartDate.getTime() + tForecast * 24 * 60 * 60 * 1000);
        forecastData.push({ date, Q: Q_forecast, phase });
      }
      forecasts[phase] = forecastData;
    });
    return forecasts;
  }, [phaseParams, tMax, maxProdDate, forecastDays]);

  const combinedForecast = useMemo(() => Object.values(allForecastPoints).flat(), [allForecastPoints]);
  const combinedAll = useMemo(() => [...allHistoricalPoints, ...combinedForecast], [allHistoricalPoints, combinedForecast]);

  // Compute estimated ultimate recovery (EUR)
  const calculatedEUR = useMemo(() => {
    const results = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      if (b === 0) {
        results[phase] = Qi / D;
      } else if (b > 0 && b < 1) {
        results[phase] = Qi / (D * (1 - b));
      } else if (b === 1) {
        const economicLimit = 1;
        results[phase] = Qi * (1 + Math.log(Qi / economicLimit) / D);
      } else {
        results[phase] = "N/A";
      }
    });
    return results;
  }, [phaseParams]);

  // Call the parent callback when parameters change.
  useEffect(() => {
    if (onParametersCalculated) {
      onParametersCalculated({ phaseParams, calculatedEUR });
    }
  }, [phaseParams, calculatedEUR, onParametersCalculated]);

  // Render chart using D3.
  useEffect(() => {
    if (!localHasData) return;
    const margin = { top: 20, right: 30, bottom: 40, left: 60 };
    const width = 800;
    const height = 400;
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();
    const [minDateDomain, maxDateDomain] = d3.extent(combinedAll, d => d.date);
    const [minQ, maxQ] = d3.extent(combinedAll, d => d.Q);
    const minYVal = minQ <= 0 ? 1e-2 : minQ;
    const xScale = d3.scaleTime()
      .domain([minDateDomain, maxDateDomain])
      .range([margin.left, width - margin.right]);
    let yScale;
    if (yScaleType === "log") {
      yScale = d3.scaleLog()
        .domain([Math.max(1e-5, minYVal), maxQ * yMultiplier])
        .range([height - margin.bottom, margin.top]);
    } else {
      yScale = d3.scaleLinear()
        .domain([0, maxQ * yMultiplier])
        .range([height - margin.bottom, margin.top]);
    }
    const xAxis = d3.axisBottom(xScale).tickFormat(d3.timeFormat("%Y-%m-%d"));
    const yAxis = yScaleType === "log"
      ? d3.axisLeft(yScale).ticks(6, "~s")
      : d3.axisLeft(yScale);
    svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis)
      .selectAll("text")
      .attr("transform", "rotate(-45)")
      .style("text-anchor", "end");
    svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);
    svg.append("text")
      .attr("x", width / 2)
      .attr("y", height - 5)
      .style("text-anchor", "middle")
      .text("Date");
    svg.append("text")
      .attr("transform", "rotate(-90)")
      .attr("x", -height / 2)
      .attr("y", 15)
      .style("text-anchor", "middle")
      .text("Production Rate");
    Object.keys(phasesDetected).forEach(phase => {
      const points = allHistoricalPoints.filter(p => p.phase === phase);
      svg.selectAll(`circle.hist-${phase}`)
        .data(points)
        .enter()
        .append("circle")
        .attr("class", `hist-${phase}`)
        .attr("cx", d => xScale(d.date))
        .attr("cy", d => yScale(d.Q))
        .attr("r", 3)
        .attr("fill", colors[phase] || "#888");
    });
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      const histLineData = [];
      const numLinePts = 100;
      for (let i = 0; i <= numLinePts; i++) {
        const frac = i / numLinePts;
        const tVal = tMax * frac;
        const QVal = calculateRate(Qi, b, D, tVal);
        const dateVal = new Date(firstDate.getTime() + tVal * 24 * 60 * 60 * 1000);
        histLineData.push({ date: dateVal, Q: QVal });
      }
      const lineGenHist = d3.line()
        .x(d => xScale(d.date))
        .y(d => yScale(d.Q))
        .curve(d3.curveMonotoneX);
      svg.append("path")
        .datum(histLineData)
        .attr("fill", "none")
        .attr("stroke", colors[phase] || "#888888")
        .attr("stroke-width", 2)
        .style("cursor", "pointer")
        .attr("d", lineGenHist)
        .call(d3.drag()
          .on("drag", event => {
            if (autoFitEnabled) setAutoFitEnabled(false);
            setPhaseParams(prevParams => {
              const newParams = { ...prevParams };
              const currParams = { ...prevParams[phase] };
              if (activeKey === "d") {
                currParams.D = Math.max(0.0001, currParams.D + event.dy * 0.001);
              } else if (activeKey === "q") {
                currParams.Qi = Math.max(1, currParams.Qi - event.dy);
              } else if (activeKey === "b") {
                currParams.b = Math.max(0, Math.min(1, currParams.b + event.dy * 0.005));
              }
              newParams[phase] = currParams;
              return newParams;
            });
          })
        );
    });
    Object.keys(allForecastPoints).forEach(phase => {
      const fData = allForecastPoints[phase];
      if (!fData || !fData.length) return;
      const lineGenForecast = d3.line()
        .x(d => xScale(d.date))
        .y(d => yScale(d.Q))
        .curve(d3.curveMonotoneX);
      svg.append("path")
        .datum(fData)
        .attr("fill", "none")
        .attr("stroke", lightenColor(colors[phase] || "#888", 1.2))
        .attr("stroke-width", 3)
        .attr("stroke-dasharray", "5,3")
        .attr("d", lineGenForecast);
    });
    svg.append("line")
      .attr("x1", xScale(maxProdDate))
      .attr("y1", margin.top)
      .attr("x2", xScale(maxProdDate))
      .attr("y2", height - margin.bottom)
      .attr("stroke", "#666")
      .attr("stroke-width", 1)
      .attr("stroke-dasharray", "5,5");
    svg.append("text")
      .attr("x", xScale(maxProdDate) + 5)
      .attr("y", margin.top + 15)
      .attr("fill", "#666")
      .text("Forecast →");
  }, [
    localHasData, firstDate, tMax, maxProdDate, data,
    phasesDetected, phaseData, phaseParams,
    colors, activeKey,
    yScaleType, yMultiplier, forecastDays,
    allHistoricalPoints, allForecastPoints, combinedAll, autoFitEnabled
  ]);

  return (
    <div className="dca-container">
      <h3>Interactive Decline Curve Analysis</h3>
      <svg ref={svgRef} width={800} height={400}></svg>
      {/* The parameters table is not rendered here – it will be provided via the callback */}
    </div>
  );
};

export default InteractiveDCAChart;
