import React, { useRef, useEffect, useState, useMemo } from 'react';
import * as d3 from 'd3';

const calculateRate = (Qi, b, D, t) => {
  if (b === 0) {
    return Qi * Math.exp(-D * t);
  }
  return Qi / Math.pow(1 + b * D * t, 1 / b);
};

const fitDeclineParameters = (data, fitWindow = 60) => {
  if (!data || data.length < 3)
    return { Qi: data?.[0]?.value || 100, b: 0.9, D: 0.01 };

  const sorted = data.slice().sort((a, b) => a.t - b.t);
  const Qi = sorted[0].value;
  const recent = sorted.slice(-Math.min(fitWindow, sorted.length));
  let bestParams = { b: 0, D: 0.01 }, bestError = Infinity;

  // Coarse search for b
  for (let b = 0; b <= 1; b += 0.1) {
    const { D, error } = findOptimalD(recent, Qi, b);
    if (error < bestError) {
      bestError = error;
      bestParams = { b, D };
    }
  }
  // Refine b
  const bmin = Math.max(0, bestParams.b - 0.1);
  const bmax = Math.min(1, bestParams.b + 0.1);
  for (let b = bmin; b <= bmax; b += 0.01) {
    const { D, error } = findOptimalD(recent, Qi, b);
    if (error < bestError) {
      bestError = error;
      bestParams = { b, D };
    }
  }
  return { Qi, b: bestParams.b, D: bestParams.D };
};

function findOptimalD(recentData, Qi, b) {
  let bestD = 0.01, bestError = Infinity;
  for (let D = 0.001; D <= 0.5; D += 0.005) {
    let err = 0;
    for (const d of recentData) {
      const pred = calculateRate(Qi, b, D, d.t);
      err += (pred - d.value) ** 2;
    }
    if (err < bestError) {
      bestError = err;
      bestD = D;
    }
  }
  // Refine around bestD
  const Dmin = Math.max(0.001, bestD - 0.005);
  const Dmax = bestD + 0.005;
  for (let D = Dmin; D <= Dmax; D += 0.0005) {
    let err = 0;
    for (const d of recentData) {
      const pred = calculateRate(Qi, b, D, d.t);
      err += (pred - d.value) ** 2;
    }
    if (err < bestError) {
      bestError = err;
      bestD = D;
    }
  }
  return { D: bestD, error: bestError };
}

// Helper functions moved out to avoid re-declaration in component
function detectDateColumn(columns) {
  const candidates = columns.filter(h => {
    const lower = h.toLowerCase();
    return (
      (lower.includes("prod") && lower.includes("date")) ||
      lower.includes("proddt") ||
      lower.includes("proddttm") ||
      lower === "date" ||
      lower === "datetime"
    );
  });
  return candidates.length > 0 ? candidates[0] : "Production_Date";
}

function detectColumns(columns) {
  let phases = {};
  columns.forEach(header => {
    const lower = header.toLowerCase();
    if (lower.includes("bopd") || (lower.includes("oil") && !phases.oil)) {
      phases.oil = header;
    }
    if (lower.includes("bwpd") || (lower.includes("water") && !phases.water)) {
      phases.water = header;
    }
    if (lower.includes("mcfd") || (lower.includes("gas") && !phases.gas)) {
      phases.gas = header;
    }
    if ((lower.includes("pip") || lower.includes("pressure") || lower.includes("psi")) && !phases.pressure) {
      phases.pressure = header;
    }
  });
  return phases;
}

export default function InteractiveDCAChart({
  dataString,
  startDate,
  endDate,
  colors,
  yScaleType,
  yMultiplier, // Always 1 from parent.
  forecastDays,
  autoForecast = true,
  onParametersCalculated
}) {
  const svgRef = useRef(null);
  const xScaleRef = useRef(null);
  // Store previous parameter values to prevent redundant updates
  const prevParamsRef = useRef({});
  const lastOnParamsCallRef = useRef(null);
  
  // For tooltip: store hover info.
  const [hoverInfo, setHoverInfo] = useState(null);

  // Parse CSV data.
  const parsedData = useMemo(() => {
    try {
      return d3.csvParse(dataString);
    } catch (err) {
      console.error("Error parsing CSV:", err);
      return [];
    }
  }, [dataString]);

  const hasData = parsedData.length > 0;
  const prodDateCol = useMemo(() => 
    hasData ? detectDateColumn(parsedData.columns) : "Production_Date", 
  [hasData, parsedData]);
  
  const phasesDetected = useMemo(() => 
    hasData ? detectColumns(parsedData.columns) : {}, 
  [hasData, parsedData]);
  
  const filteredData = useMemo(() => {
    if (!hasData) return [];
    let arr = parsedData.map(row => ({
      ...row,
      [prodDateCol]: new Date(row[prodDateCol])
    })).filter(row => !isNaN(row[prodDateCol].getTime()));
    arr.sort((a, b) => a[prodDateCol] - b[prodDateCol]);
    if (startDate) {
      const sd = new Date(startDate);
      arr = arr.filter(row => row[prodDateCol] >= sd);
    }
    if (endDate) {
      const ed = new Date(endDate);
      arr = arr.filter(row => row[prodDateCol] <= ed);
    }
    return arr;
  }, [parsedData, prodDateCol, startDate, endDate, hasData]);

  const localHasData = filteredData.length > 0;
  const firstDate = useMemo(() => 
    localHasData ? filteredData[0][prodDateCol] : new Date(),
  [localHasData, filteredData, prodDateCol]);

  const data = useMemo(() => {
    if (!localHasData) return [];
    return filteredData.map(row => {
      const date = row[prodDateCol];
      return { ...row, t: (date - firstDate) / (1000 * 60 * 60 * 24) };
    });
  }, [filteredData, localHasData, firstDate, prodDateCol]);

  const phaseData = useMemo(() => {
    let result = {};
    Object.keys(phasesDetected).forEach(phase => {
      result[phase] = data.map(d => ({
        t: d.t,
        value: +d[phasesDetected[phase]]
      })).filter(d => !isNaN(d.value));
    });
    return result;
  }, [data, phasesDetected]);

  const tMax = useMemo(() => 
    localHasData ? d3.max(data, d => d.t) : 0,
  [data, localHasData]);
  
  const lastProdDate = useMemo(() => 
    localHasData ? d3.max(data, d => d[prodDateCol]) : new Date(),
  [data, localHasData, prodDateCol]);

  const [phaseParams, setPhaseParams] = useState({});
  const [autoFitEnabled, setAutoFitEnabled] = useState(true);
  const [activeKey, setActiveKey] = useState(null);

  // Key events for adjusting parameters.
  useEffect(() => {
    const handleKeyDown = (e) => {
      const key = e.key.toLowerCase();
      if (["d", "q", "b"].includes(key)) setActiveKey(key);
    };
    const handleKeyUp = () => setActiveKey(null);
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  // Auto-fit decline parameters with memoized input dependencies
  useEffect(() => {
    if (!localHasData) return;
    
    let newParams = {};
    Object.keys(phaseData).forEach(phase => {
      const arr = phaseData[phase];
      if (arr.length < 3) {
        newParams[phase] = { Qi: arr?.[0]?.value || 100, b: 0.9, D: 0.01 };
      } else {
        newParams[phase] = fitDeclineParameters(arr);
      }
    });
    
    // Only update state if parameters have actually changed and autofit is enabled
    if (autoFitEnabled && JSON.stringify(newParams) !== JSON.stringify(prevParamsRef.current)) {
      prevParamsRef.current = newParams;
      setPhaseParams(newParams);
    }
  }, [phaseData, localHasData, autoFitEnabled]);

  const calculatedEUR = useMemo(() => {
    let results = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      if (b === 0) results[phase] = Qi / D;
      else if (b > 0 && b < 1) results[phase] = Qi / (D * (1 - b));
      else if (b === 1) results[phase] = Qi * 10000;
      else results[phase] = "N/A";
    });
    return results;
  }, [phaseParams]);

  // Compute forecast average for first 60 days.
  const forecastAverage = useMemo(() => {
    let fa = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      const Q_start = calculateRate(Qi, b, D, tMax);
      const steps = 60;
      let sum = 0;
      for (let i = 0; i <= steps; i++) {
        const t = (i / steps) * 60;
        const Q = b === 0 ? Q_start * Math.exp(-D * t) : Q_start / Math.pow(1 + b * D * t, 1 / b);
        sum += Q;
      }
      fa[phase] = sum / (steps + 1);
    });
    return fa;
  }, [phaseParams, tMax]);

  // FIX: Use useEffect with proper comparison to prevent infinite loops
  useEffect(() => {
    if (onParametersCalculated) {
      const newParams = { phaseParams, calculatedEUR, forecastAverage };
      const stringifiedNew = JSON.stringify(newParams);
      const stringifiedPrev = JSON.stringify(lastOnParamsCallRef.current);
      
      // Only call the callback if parameters have actually changed
      if (stringifiedNew !== stringifiedPrev) {
        lastOnParamsCallRef.current = newParams;
        onParametersCalculated(newParams);
      }
    }
  }, [phaseParams, calculatedEUR, forecastAverage, onParametersCalculated]);

  const allHistoricalPoints = useMemo(() => {
    let out = [];
    Object.keys(phaseData).forEach(phase => {
      phaseData[phase].forEach(d => {
        out.push({
          date: new Date(firstDate.getTime() + d.t * 86400000),
          Q: d.value,
          phase
        });
      });
    });
    return out;
  }, [phaseData, firstDate]);

  function calcForecast(Qi, b, D, tMaxVal, days) {
    let Qstart = calculateRate(Qi, b, D, tMaxVal);
    let arr = [];
    const steps = 50;
    for (let i = 0; i <= steps; i++) {
      const dtFraction = (i / steps) * days;
      const q = b === 0 ? Qstart * Math.exp(-D * dtFraction) : Qstart / Math.pow(1 + b * D * dtFraction, 1 / b);
      const dt = new Date(lastProdDate.getTime() + dtFraction * 86400000);
      arr.push({ date: dt, Q: q });
    }
    return arr;
  }

  const allForecastPoints = useMemo(() => {
    let out = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      if (!Qi || D <= 0) {
        out[phase] = [];
      } else {
        out[phase] = calcForecast(Qi, b, D, tMax, forecastDays).map(obj => ({
          ...obj,
          phase
        }));
      }
    });
    return out;
  }, [phaseParams, tMax, forecastDays, lastProdDate]);

  const combinedAll = useMemo(() => {
    let arr = [...allHistoricalPoints];
    Object.values(allForecastPoints).forEach(a => (arr = arr.concat(a)));
    return arr;
  }, [allHistoricalPoints, allForecastPoints]);

  // D3 rendering with careful dependency tracking
  useEffect(() => {
    if (!localHasData) return;
    
    const margin = { top: 20, right: 30, bottom: 40, left: 60 };
    const width = 800;
    const height = 400;
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    // Handle possible empty arrays with sensible defaults
    const dateExtent = d3.extent(combinedAll, d => d.date);
    const minDateDomain = dateExtent[0] || new Date();
    const maxDateDomain = dateExtent[1] || new Date();
    
    const qExtent = d3.extent(combinedAll, d => d.Q);
    let minQ = qExtent[0] || 0;
    let maxQ = qExtent[1] || 100;
    
    if (yScaleType === "log" && minQ <= 0) minQ = 1e-2;
    
    const xScale = d3.scaleTime()
      .domain([minDateDomain, maxDateDomain])
      .range([margin.left, width - margin.right]);
      
    xScaleRef.current = xScale;
    
    const yDomain = yScaleType === "log" ? [Math.max(1e-5, minQ), maxQ] : [0, maxQ];
    const yScale = yScaleType === "log"
      ? d3.scaleLog().domain(yDomain).range([height - margin.bottom, margin.top])
      : d3.scaleLinear().domain(yDomain).range([height - margin.bottom, margin.top]);

    const xAxis = d3.axisBottom(xScale).tickFormat(d3.timeFormat("%Y-%m-%d"));
    const yAxis = yScaleType === "log" ? d3.axisLeft(yScale).ticks(6, "~s") : d3.axisLeft(yScale);
    
    svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis)
      .selectAll("text")
      .attr("transform", "rotate(-45)")
      .style("text-anchor", "end");
      
    svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);
      
    svg.append("text")
      .attr("x", width / 2)
      .attr("y", height - 5)
      .style("text-anchor", "middle")
      .text("Date");
      
    svg.append("text")
      .attr("transform", "rotate(-90)")
      .attr("x", -height / 2)
      .attr("y", 15)
      .style("text-anchor", "middle")
      .text("Production Rate");

    // Plot historical points with NaN safety checks
    Object.keys(phasesDetected).forEach(phase => {
      const pts = allHistoricalPoints.filter(o => o.phase === phase);
      svg.selectAll(`circle.hist-${phase}`)
        .data(pts)
        .enter()
        .append("circle")
        .attr("cx", d => {
          const x = xScale(d.date);
          return isNaN(x) ? margin.left : x; // Prevent NaN
        })
        .attr("cy", d => {
          const y = yScale(d.Q);
          return isNaN(y) ? height - margin.bottom : y; // Prevent NaN
        })
        .attr("r", 3)
        .attr("fill", colors[phase] || "#888");
    });

    // Plot decline lines with drag behavior.
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      const lineData = [];
      const steps = 100;
      for (let i = 0; i <= steps; i++) {
        const frac = i / steps;
        const tVal = tMax * frac;
        const QVal = calculateRate(Qi, b, D, tVal);
        const dt = new Date(firstDate.getTime() + tVal * 86400000);
        lineData.push({ date: dt, Q: QVal });
      }
      
      // Only draw the line if we have valid data
      if (lineData.length > 1) {
        const lineGen = d3.line()
          .x(d => {
            const x = xScale(d.date);
            return isNaN(x) ? 0 : x; // Prevent NaN
          })
          .y(d => {
            const y = yScale(d.Q);
            return isNaN(y) ? 0 : y; // Prevent NaN
          })
          .curve(d3.curveMonotoneX);
          
        svg.append("path")
          .datum(lineData)
          .attr("fill", "none")
          .attr("stroke", colors[phase] || "#888888")
          .attr("stroke-width", 2)
          .style("cursor", "pointer")
          .attr("d", lineGen)
          .call(
            d3.drag().on("drag", (event) => {
              if (autoFitEnabled) setAutoFitEnabled(false);
              setPhaseParams(prev => {
                const copy = { ...prev };
                const curr = { ...copy[phase] };
                if (activeKey === "d") {
                  curr.D = Math.max(0.0001, curr.D + event.dy * 0.001);
                } else if (activeKey === "q") {
                  curr.Qi = Math.max(1, curr.Qi - event.dy);
                } else if (activeKey === "b") {
                  curr.b = Math.max(0, Math.min(1, curr.b + event.dy * 0.005));
                }
                copy[phase] = curr;
                return copy;
              });
            })
          );
      }
    });

    // Plot forecast lines with hover/drag effects.
    Object.keys(allForecastPoints).forEach(phase => {
      const fc = allForecastPoints[phase];
      if (fc.length > 1) {
        const lineGenFc = d3.line()
          .x(d => {
            const x = xScale(d.date);
            return isNaN(x) ? 0 : x;
          })
          .y(d => {
            const y = yScale(d.Q);
            return isNaN(y) ? 0 : y;
          })
          .curve(d3.curveMonotoneX);
          
        svg.append("path")
          .datum(fc)
          .attr("fill", "none")
          .attr("stroke", d3.color(colors[phase] || "#888").brighter(1.2))
          .attr("stroke-width", 3)
          .attr("stroke-dasharray", "5,3")
          .attr("d", lineGenFc)
          .on("mouseover", function() {
            d3.select(this).attr("stroke-width", 5);
          })
          .on("mouseout", function() {
            d3.select(this).attr("stroke-width", 3);
          })
          .on("mousedown", function() {
            d3.select(this).style("cursor", "grabbing");
          })
          .on("mouseup", function() {
            d3.select(this).style("cursor", "grab");
          });
      }
    });

    svg.append("line")
      .attr("x1", xScale(lastProdDate))
      .attr("y1", margin.top)
      .attr("x2", xScale(lastProdDate))
      .attr("y2", height - margin.bottom)
      .attr("stroke", "#666")
      .attr("stroke-width", 1)
      .attr("stroke-dasharray", "5,5");
      
    svg.append("text")
      .attr("x", xScale(lastProdDate) + 5)
      .attr("y", margin.top + 15)
      .attr("fill", "#666")
      .text("Forecast →");

    // d3-zoom for vertical scaling (only update DOM, no state changes)
    const zoomBehavior = d3.zoom()
      .scaleExtent([0.5, 10])
      .on("zoom", (event) => {
        const newYScale = event.transform.rescaleY(yScale);
        
        // Update circle positions
        svg.selectAll("circle").attr("cy", d => {
          const y = newYScale(d.Q);
          return isNaN(y) ? height - margin.bottom : y;
        });
        
        // Update line paths
        svg.selectAll("path").each(function(d) {
          if (Array.isArray(d)) {
            const newLineGen = d3.line()
              .x(dd => {
                const x = xScale(dd.date);
                return isNaN(x) ? 0 : x;
              })
              .y(dd => {
                const y = newYScale(dd.Q);
                return isNaN(y) ? 0 : y;
              })
              .curve(d3.curveMonotoneX);
              
            d3.select(this).attr("d", newLineGen(d));
          }
        });
        
        // Update y-axis
        svg.select("g").call(yAxis.scale(newYScale));
      });
      
    svg.call(zoomBehavior);
  }, [
    localHasData,
    combinedAll,
    firstDate,
    lastProdDate,
    yScaleType,
    forecastDays,
    autoFitEnabled,
    tMax,
    activeKey,
    phaseParams,
    colors,
    phasesDetected,
    allHistoricalPoints,
    allForecastPoints
  ]);

  return (
    <div style={{ position: 'relative' }}>
      <svg
        ref={svgRef}
        width={800}
        height={400}
        onMouseMove={(event) => {
          if (!localHasData || !xScaleRef.current) return;
          
          const [mouseX] = d3.pointer(event, svgRef.current);
          const date = xScaleRef.current.invert(mouseX);
          const t = (date - firstDate) / 86400000;
          
          let vals = {};
          Object.keys(phaseParams).forEach(phase => {
            const { Qi, b, D } = phaseParams[phase] || {};
            if (Qi && b !== undefined && D) {
              vals[phase] = calculateRate(Qi, b, D, t);
            }
          });
          
          setHoverInfo({ x: mouseX, date, values: vals });
        }}
        onMouseOut={() => setHoverInfo(null)}
      />
      {hoverInfo && (
        <div
          style={{
            position: 'absolute',
            left: hoverInfo.x + 5,
            top: 0,
            pointerEvents: 'none',
            background: 'rgba(255,255,255,0.9)',
            border: '1px solid #ccc',
            padding: 5,
            fontSize: 12,
            borderRadius: 4
          }}
        >
          <div>{hoverInfo.date.toDateString()}</div>
          {Object.keys(hoverInfo.values).map(ph => (
            <div key={ph}>
              {ph.toUpperCase()}: {hoverInfo.values[ph].toFixed(2)}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
