import React, { useState, useEffect, useMemo } from 'react';
import queryString from 'query-string';
import * as XLSX from 'xlsx';
import * as d3 from 'd3';
import InteractiveDCAChart from './InteractiveDCAChart';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

// Helper: Auto-detect the production date column.
function detectDateColumn(headers) {
  const candidates = headers.filter(h => {
    const lower = h.toLowerCase();
    return (
      (lower.includes("prod") && lower.includes("date")) ||
      lower.includes("proddt") ||
      lower.includes("proddttm") ||
      lower === "date" ||
      lower === "datetime"
    );
  });
  return candidates.length > 0 ? candidates[0] : "Production_Date";
}

function App() {
  // Basic state.
  const [secret, setSecret] = useState('');
  const [dataString, setDataString] = useState('');
  const [response, setResponse] = useState(null);
  const [error, setError] = useState(null);

  // Date filter state.
  const [minDate, setMinDate] = useState(null);
  const [maxDate, setMaxDate] = useState(null);
  const [dateRange, setDateRange] = useState([0, 0]);

  // Toggles.
  const [showDataInput, setShowDataInput] = useState(false);
  const [showParameters, setShowParameters] = useState(false);
  const [showInstructions, setShowInstructions] = useState(false);

  // Sidebar controls.
  const [colors, setColors] = useState({
    oil: "#008000",
    water: "#0000ff",
    gas: "#ff0000",
    pressure: "#000000"
  });
  const [yScaleType, setYScaleType] = useState("linear");
  const [yMultiplier, setYMultiplier] = useState(1.1);
  const [forecastDays, setForecastDays] = useState(90);

  // Chart reset key.
  const [chartKey, setChartKey] = useState(0);
  const resetAutoFit = () => {
    setChartKey(prev => prev + 1);
  };

  // Computed parameters from chart.
  const [chartParams, setChartParams] = useState(null);

  // 60-day averages.
  const [show60DayAverages, setShow60DayAverages] = useState(false);
  const [sixtyDayAverages, setSixtyDayAverages] = useState(null);
  const [sixtyDayJSON, setSixtyDayJSON] = useState("");

  // Parse query parameters on mount.
  useEffect(() => {
    const params = queryString.parse(window.location.search);
    if (params.secret) setSecret(params.secret);
    if (params.data) {
      setDataString(params.data);
      setShowDataInput(false);
    }
  }, []);

  // When CSV data changes, parse to set min and max dates.
  useEffect(() => {
    if (!dataString) return;
    try {
      const parsed = d3.csvParse(dataString);
      if (parsed && parsed.length > 0 && parsed.columns) {
        const dateCol = detectDateColumn(parsed.columns);
        const dates = parsed.map(row => new Date(row[dateCol])).filter(d => !isNaN(d));
        if (dates.length > 0) {
          const minD = new Date(Math.min(...dates));
          const maxD = new Date(Math.max(...dates));
          setMinDate(minD);
          setMaxDate(maxD);
          const totalDays = Math.ceil((maxD.getTime() - minD.getTime()) / (1000 * 60 * 60 * 24));
          setDateRange([0, totalDays]);
        }
      }
    } catch (e) {
      console.error("Error parsing dates", e);
    }
  }, [dataString]);

  // File upload.
  function handleFileUpload(e) {
    const file = e.target.files[0];
    if (!file) return;
    const fileName = file.name.toLowerCase();
    const reader = new FileReader();
    if (fileName.endsWith('.csv') || fileName.endsWith('.txt')) {
      reader.onload = (ev) => setDataString(ev.target.result);
      reader.readAsText(file);
    } else if (fileName.endsWith('.xls') || fileName.endsWith('.xlsx')) {
      reader.onload = (ev) => {
        const data = new Uint8Array(ev.target.result);
        const workbook = XLSX.read(data, { type: 'array' });
        const firstSheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[firstSheetName];
        const csv = XLSX.utils.sheet_to_csv(worksheet);
        setDataString(csv);
      };
      reader.readAsArrayBuffer(file);
    } else {
      alert('Unsupported file format. Please upload CSV, TXT, XLS, or XLSX.');
    }
  }

  // Submit data.
  async function handleSubmit() {
    if (!secret || !dataString) {
      setError('Secret key and data are required.');
      return;
    }
    try {
      const res = await fetch('http://127.0.0.1:5000/api/validate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ secret, data: dataString })
      });
      const json = await res.json();
      if (json.error) {
        setError(json.error);
        setResponse(null);
      } else {
        setResponse(json);
        setError(null);
      }
    } catch (err) {
      setError('Error connecting to backend: ' + err.toString());
      setResponse(null);
    }
  }

  const totalDays = minDate && maxDate
    ? Math.ceil((maxDate.getTime() - minDate.getTime()) / (1000 * 60 * 60 * 24))
    : 0;

  const filteredStartDate = minDate
    ? new Date(minDate.getTime() + dateRange[0] * 24 * 60 * 60 * 1000)
    : null;
  const filteredEndDate = minDate
    ? new Date(minDate.getTime() + dateRange[1] * 24 * 60 * 60 * 1000)
    : null;

  // Compute 60-day averages.
  const csvParsed = useMemo(() => {
    try {
      return d3.csvParse(dataString);
    } catch (err) {
      return [];
    }
  }, [dataString]);

  const phasesDetected = useMemo(() => {
    return (csvParsed && csvParsed.length > 0)
      ? detectColumns(csvParsed.columns)
      : {};
  }, [csvParsed]);

  function calculate60DayAvg(data, phases) {
    let results = { oil: 0, water: 0, gas: 0, pressure: 0 };
    let counters = { oil: 0, water: 0, gas: 0, pressure: 0 };
    data.forEach(row => {
      if (row.t <= 60) {
        if (phases.oil && !isNaN(+row[phases.oil])) {
          results.oil += +row[phases.oil];
          counters.oil++;
        }
        if (phases.water && !isNaN(+row[phases.water])) {
          results.water += +row[phases.water];
          counters.water++;
        }
        if (phases.gas && !isNaN(+row[phases.gas])) {
          results.gas += +row[phases.gas];
          counters.gas++;
        }
        if (phases.pressure && !isNaN(+row[phases.pressure])) {
          results.pressure += +row[phases.pressure];
          counters.pressure++;
        }
      }
    });
    Object.keys(results).forEach(key => {
      results[key] = counters[key] > 0 ? results[key] / counters[key] : 0;
    });
    return results;
  }

  useEffect(() => {
    if (!csvParsed || csvParsed.length === 0 || !minDate) return;
    const dateCol = csvParsed.columns ? detectDateColumn(csvParsed.columns) : "Production_Date";
    const localData = csvParsed.map(row => {
      const dt = new Date(row[dateCol]);
      return { ...row, t: (dt - minDate) / (1000 * 60 * 60 * 24) };
    });
    const result = calculate60DayAvg(localData, phasesDetected);
    setSixtyDayAverages(result);
    setSixtyDayJSON(JSON.stringify(result, null, 2));
  }, [csvParsed, minDate, phasesDetected]);

  return (
    <div style={{ padding: 20, fontFamily: 'Arial, sans-serif' }}>
      <h2>Custom DCA Application</h2>

      {/* Top row: horizontally aligned controls */}
      <div style={{ display: 'flex', alignItems: 'center', gap: 10, marginBottom: 10 }}>
        <label>
          Secret Key:&nbsp;
          <input
            type="text"
            value={secret}
            onChange={(e) => setSecret(e.target.value)}
            placeholder="Enter secret key"
            style={{ width: '200px' }}
          />
        </label>
        <label>
          Upload CSV/Excel:&nbsp;
          <input type="file" onChange={handleFileUpload} />
        </label>
        <button onClick={() => setShowDataInput(prev => !prev)}>
          {showDataInput ? 'Hide CSV Data' : 'Show CSV Data'}
        </button>
        <button onClick={handleSubmit}>Submit Data</button>
      </div>

      {showDataInput && (
        <div style={{ marginBottom: 10 }}>
          <textarea
            value={dataString}
            onChange={(e) => setDataString(e.target.value)}
            placeholder="Paste CSV data here..."
            rows="6"
            cols="80"
          ></textarea>
        </div>
      )}

      {error && <div style={{ color: 'red', marginBottom: 10 }}>{error}</div>}
      {response && (
        <div style={{ marginBottom: 10 }}>
          <h4>Data Successfully Processed</h4>
          <p>Columns: {response.columns.join(', ')}</p>
          <p>Rows: {response.rows}</p>
        </div>
      )}

      {/* Show 60-day averages overlay */}
      <div style={{ marginBottom: 10 }}>
        <button onClick={() => setShow60DayAverages(prev => !prev)}>
          {show60DayAverages ? 'Hide 60-Day Averages' : 'Show 60-Day Averages'}
        </button>
      </div>
      {show60DayAverages && sixtyDayAverages && (
        <div style={{
          background: '#f9f9f9',
          border: '1px solid #ccc',
          padding: 10,
          marginBottom: 10,
          borderRadius: 4
        }}>
          <h4 style={{ marginTop: 0 }}>60-Day Averages</h4>
          <pre style={{ margin: 0 }}>{sixtyDayJSON}</pre>
        </div>
      )}

      {/* Main layout: Chart & Sidebar */}
      <div style={{ display: 'flex', gap: 20 }}>
        {/* Chart Area */}
        <div style={{ flexGrow: 1, position: 'relative' }}>
          {/* Overlay buttons on chart */}
          <div style={{ position: 'absolute', top: 10, left: 10, zIndex: 10, display: 'flex', gap: '10px' }}>
            <button 
              onClick={() => setShowInstructions(prev => !prev)}
              style={{
                padding: '6px 12px',
                border: 'none',
                background: '#666',
                color: '#fff',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              {showInstructions ? 'Hide Instructions' : 'Show Instructions'}
            </button>
            <button 
              onClick={() => setShowParameters(prev => !prev)}
              style={{
                padding: '6px 12px',
                border: 'none',
                background: '#1890ff',
                color: '#fff',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              {showParameters ? 'Hide Parameters' : 'Show Parameters'}
            </button>
          </div>

          {/* Parameters overlay */}
          {showParameters && chartParams && (
            <div style={{
              position: 'absolute',
              top: 50,
              right: 10,
              background: 'rgba(255,255,255,0.95)',
              border: '1px solid #ccc',
              padding: 10,
              zIndex: 10,
              maxWidth: 300,
              borderRadius: 4
            }}>
              <h4 style={{ marginTop: 0 }}>Decline Parameters</h4>
              <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                  <tr>
                    <th style={{ border: '1px solid #ddd', padding: '4px' }}>Phase</th>
                    <th style={{ border: '1px solid #ddd', padding: '4px' }}>Qi</th>
                    <th style={{ border: '1px solid #ddd', padding: '4px' }}>b</th>
                    <th style={{ border: '1px solid #ddd', padding: '4px' }}>D</th>
                    <th style={{ border: '1px solid #ddd', padding: '4px' }}>EUR</th>
                  </tr>
                </thead>
                <tbody>
                  {chartParams.phaseParams && Object.keys(chartParams.phaseParams).map(phase => (
                    <tr key={phase}>
                      <td style={{ border: '1px solid #ddd', padding: '4px' }}>{phase.toUpperCase()}</td>
                      <td style={{ border: '1px solid #ddd', padding: '4px' }}>
                        {chartParams.phaseParams[phase].Qi.toFixed(2)}
                      </td>
                      <td style={{ border: '1px solid #ddd', padding: '4px' }}>
                        {chartParams.phaseParams[phase].b.toFixed(3)}
                      </td>
                      <td style={{ border: '1px solid #ddd', padding: '4px' }}>
                        {chartParams.phaseParams[phase].D.toFixed(4)}
                      </td>
                      <td style={{ border: '1px solid #ddd', padding: '4px' }}>
                        {typeof chartParams.calculatedEUR[phase] === 'number'
                          ? chartParams.calculatedEUR[phase].toFixed(0)
                          : chartParams.calculatedEUR[phase]}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <button onClick={() => setShowParameters(false)} style={{ marginTop: 10 }}>
                Close
              </button>
            </div>
          )}

          {/* Instructions overlay */}
          {showInstructions && (
            <div style={{
              position: 'absolute',
              top: 50,
              left: 10,
              background: 'rgba(255,255,255,0.95)',
              border: '1px solid #ccc',
              padding: 10,
              zIndex: 10,
              maxWidth: 300,
              borderRadius: 4
            }}>
              <h4 style={{ marginTop: 0 }}>Interactive Controls</h4>
              <p>
                Hold <strong>D</strong> and drag vertically to adjust decline rate (D).<br />
                Hold <strong>Q</strong> and drag vertically to adjust initial rate (Qi).<br />
                Hold <strong>B</strong> and drag vertically to adjust decline exponent (b).<br />
                (Dragging disables autofit until reset.)<br />
                You can also scroll/drag vertically in the chart to zoom the y-axis.
              </p>
              <button onClick={() => setShowInstructions(false)} style={{ marginTop: 10 }}>
                Close
              </button>
            </div>
          )}

          {/* Interactive Chart */}
          <InteractiveDCAChart
            key={chartKey}
            dataString={dataString}
            startDate={filteredStartDate ? filteredStartDate.toISOString().slice(0, 10) : ""}
            endDate={filteredEndDate ? filteredEndDate.toISOString().slice(0, 10) : ""}
            colors={colors}
            yScaleType={yScaleType}
            yMultiplier={yMultiplier}
            forecastDays={forecastDays}
            onParametersCalculated={setChartParams}
          />

          {/* Dual-range slider */}
          <div style={{ marginTop: 10 }}>
            <label>
              Production Date Range:
              <Slider.Range 
                min={0}
                max={totalDays}
                value={dateRange}
                onChange={(newRange) => setDateRange(newRange)}
                tipFormatter={value => {
                  if (!minDate) return '';
                  const d = new Date(minDate.getTime() + value * 24 * 60 * 60 * 1000);
                  return d.toDateString();
                }}
              />
            </label>
            <div>
              {filteredStartDate ? filteredStartDate.toDateString() : 'N/A'}  {filteredEndDate ? filteredEndDate.toDateString() : 'N/A'}
            </div>
          </div>
        </div>

        {/* Sidebar */}
        <div style={{ width: 250 }}>
          <div style={{ marginBottom: 20 }}>
            <h4>Phase Colors</h4>
            {['oil', 'water', 'gas', 'pressure'].map(phase => (
              <div key={phase} style={{ marginBottom: 5 }}>
                <label>
                  {phase.toUpperCase()}:
                  <input
                    type="color"
                    value={colors[phase]}
                    onChange={(e) => setColors(prev => ({ ...prev, [phase]: e.target.value }))}
                    style={{ marginLeft: 5 }}
                  />
                </label>
              </div>
            ))}
          </div>
          <div style={{ marginBottom: 20 }}>
            <h4>Axis Controls</h4>
            <div style={{ marginBottom: 10 }}>
              <label>
                <input 
                  type="radio"
                  name="yScaleType"
                  value="linear"
                  checked={yScaleType === "linear"}
                  onChange={() => setYScaleType("linear")}
                /> Linear
              </label>
              <label style={{ marginLeft: 10 }}>
                <input 
                  type="radio"
                  name="yScaleType"
                  value="log"
                  checked={yScaleType === "log"}
                  onChange={() => setYScaleType("log")}
                /> Log
              </label>
            </div>
            <div style={{ marginBottom: 10 }}>
              <label>
                Y-Axis Multiplier: {yMultiplier.toFixed(2)}
                <input
                  type="range"
                  min="1"
                  max="3"
                  step="0.05"
                  value={yMultiplier}
                  onChange={(e) => setYMultiplier(+e.target.value)}
                  style={{ width: '100%' }}
                />
              </label>
            </div>
            <div>
              <label>
                Forecast Extension (Days): {forecastDays}
                <input
                  type="range"
                  min="30"
                  max="365"
                  step="30"
                  value={forecastDays}
                  onChange={(e) => setForecastDays(+e.target.value)}
                  style={{ width: '100%' }}
                />
              </label>
            </div>
          </div>
          <div>
            <button 
              onClick={resetAutoFit}
              style={{
                padding: '6px 12px',
                border: 'none',
                background: '#fa541c',
                color: '#fff',
                borderRadius: '4px',
                cursor: 'pointer'
              }}
            >
              Reset Autofit
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
