import React, { useRef, useEffect, useState, useMemo } from 'react';
import * as d3 from 'd3';

// Helper: Darken a HEX color by a given factor.
const darkenColor = (hex, factor = 0.8) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.floor(c.r * factor);
    c.g = Math.floor(c.g * factor);
    c.b = Math.floor(c.b * factor);
    return c.formatHex();
  }
  return hex;
};

// Helper: Lighten a HEX color by a given factor.
const lightenColor = (hex, factor = 1.2) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.min(255, Math.floor(c.r * factor));
    c.g = Math.min(255, Math.floor(c.g * factor));
    c.b = Math.min(255, Math.floor(c.b * factor));
    return c.formatHex();
  }
  return hex;
};

// Auto-detect the production date column from common names.
const detectDateColumn = (headers) => {
  const candidates = headers.filter(h => {
    const lower = h.toLowerCase();
    return (lower.includes("prod") && lower.includes("date")) ||
           lower.includes("proddt") ||
           lower.includes("proddttm") ||
           lower === "date" ||
           lower === "datetime";
  });
  return candidates.length > 0 ? candidates[0] : "Production_Date";
};

// Auto-detect measurement columns using keywords.
const detectColumns = (headers) => {
  let phases = {};
  headers.forEach(header => {
    const lower = header.toLowerCase();
    // For oil: prefer "bopd", otherwise any "oil".
    if (lower.includes("bopd")) {
      phases.oil = header;
    } else if (lower.includes("oil") && !phases.oil) {
      phases.oil = header;
    }
    // For water: prefer "bwpd", otherwise any "water".
    if (lower.includes("bwpd")) {
      phases.water = header;
    } else if (lower.includes("water") && !phases.water) {
      phases.water = header;
    }
    // For gas: prefer "mcfd", otherwise any "gas".
    if (lower.includes("mcfd")) {
      phases.gas = header;
    } else if (lower.includes("gas") && !phases.gas) {
      phases.gas = header;
    }
    // For pressure: look for "pip", "pressure", or "psi".
    if ((lower.includes("pip") || lower.includes("pressure") || lower.includes("psi")) && !phases.pressure) {
      phases.pressure = header;
    }
  });
  return phases;
};

// Improved best-fit parameter calculation using Arps decline curve models
const fitDeclineParameters = (data, fitWindow = 60) => {
  if (!data || data.length < 3) return { Qi: data?.[0]?.value || 100, b: 0.9, D: 0.01 };
  
  // Sort data by time and get the initial rate
  const sortedData = [...data].sort((a, b) => a.t - b.t);
  const Qi = sortedData[0].value;
  
  // Use recent data for fitting to prioritize latest trends
  const recentData = sortedData.slice(-Math.min(fitWindow, sortedData.length));
  
  // Try different b values and find the one with least error
  let bestB = 0;
  let bestD = 0.01;
  let bestError = Infinity;
  
  // First, try to find optimal b parameter (decline exponent)
  for (let b = 0; b <= 1; b += 0.1) {
    // For each b, find the best D
    const { D, error } = findOptimalD(recentData, Qi, b);
    
    if (error < bestError) {
      bestError = error;
      bestB = b;
      bestD = D;
    }
  }
  
  // Refine b with a finer step around the best value
  const minB = Math.max(0, bestB - 0.1);
  const maxB = Math.min(1, bestB + 0.1);
  
  for (let b = minB; b <= maxB; b += 0.01) {
    const { D, error } = findOptimalD(recentData, Qi, b);
    
    if (error < bestError) {
      bestError = error;
      bestB = b;
      bestD = D;
    }
  }
  
  return { Qi, b: bestB, D: bestD };
};

// Helper function to find optimal D for a given b
const findOptimalD = (data, Qi, b) => {
  let bestD = 0.01;
  let bestError = Infinity;
  
  // Try different D values
  for (let D = 0.001; D <= 0.5; D += 0.005) {
    let error = 0;
    
    // Calculate mean squared error
    data.forEach(d => {
      const predicted = calculateRate(Qi, b, D, d.t);
      error += Math.pow(predicted - d.value, 2);
    });
    
    if (error < bestError) {
      bestError = error;
      bestD = D;
    }
  }
  
  // Refine D with finer step
  const minD = Math.max(0.001, bestD - 0.005);
  const maxD = bestD + 0.005;
  
  for (let D = minD; D <= maxD; D += 0.0005) {
    let error = 0;
    
    data.forEach(d => {
      const predicted = calculateRate(Qi, b, D, d.t);
      error += Math.pow(predicted - d.value, 2);
    });
    
    if (error < bestError) {
      bestError = error;
      bestD = D;
    }
  }
  
  return { D: bestD, error: bestError };
};

// Calculate production rate using Arps equation
const calculateRate = (Qi, b, D, t) => {
  if (b === 0) {
    // Exponential decline
    return Qi * Math.exp(-D * t);
  } else {
    // Hyperbolic decline
    return Qi / Math.pow(1 + b * D * t, 1 / b);
  }
};

const InteractiveDCAChart = ({ dataString, startDate, endDate, autoForecast = true }) => {
  // 1) Parse CSV data
  const parsedData = useMemo(() => {
    try {
      return d3.csvParse(dataString);
    } catch (err) {
      console.error("Error parsing CSV:", err);
      return [];
    }
  }, [dataString]);

  const hasData = parsedData && parsedData.length > 0;
  const svgRef = useRef(null);

  // 2) Detect columns
  const prodDateCol = hasData ? detectDateColumn(parsedData.columns) : "Production_Date";
  const phasesDetected = hasData ? detectColumns(parsedData.columns) : {};

  // 3) Filter data by date
  const filteredData = useMemo(() => {
    if (!hasData) return [];
    let dataWithDates = parsedData.map(row => ({
      ...row,
      [prodDateCol]: new Date(row[prodDateCol])
    }));

    // valid dates only
    dataWithDates = dataWithDates.filter(row => {
      const d = new Date(row[prodDateCol]);
      return !isNaN(d.getTime());
    });

    // sort by date
    dataWithDates.sort((a, b) => a[prodDateCol] - b[prodDateCol]);

    // optional date range filter
    if (startDate) {
      const sd = new Date(startDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] >= sd);
    }
    if (endDate) {
      const ed = new Date(endDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] <= ed);
    }
    return dataWithDates;
  }, [parsedData, startDate, endDate, hasData, prodDateCol]);

  const localHasData = filteredData && filteredData.length > 0;
  const firstDate = localHasData ? filteredData[0][prodDateCol] : new Date();
  
  // t = days since firstDate
  const data = useMemo(() => {
    if (!localHasData) return [];
    return filteredData.map(row => {
      const date = row[prodDateCol];
      return {
        ...row,
        Production_Date: date,
        t: (date - firstDate) / (1000 * 60 * 60 * 24)
      };
    });
  }, [filteredData, prodDateCol, localHasData, firstDate]);

  // 4) Build phase data (array of {t, value} for each phase)
  const phaseData = useMemo(() => {
    const result = {};
    Object.keys(phasesDetected).forEach(phase => {
      result[phase] = localHasData
        ? data.map(d => ({
            t: d.t,
            value: +d[phasesDetected[phase]]
          })).filter(d => !isNaN(d.value))
        : [];
    });
    return result;
  }, [data, phasesDetected, localHasData]);

  // 5) Calculate tMax, last production date
  const tMax = localHasData ? d3.max(data, d => d.t) : 0;
  const maxProdDate = localHasData
    ? d3.max(data, d => d.Production_Date)
    : new Date();

  // 6) Auto-fit optimal parameters for each phase
  const initialParams = useMemo(() => {
    const params = {};
    if (localHasData) {
      Object.keys(phaseData).forEach(phase => {
        const series = phaseData[phase];
        if (!series || series.length < 3) {
          params[phase] = { Qi: series?.[0]?.value || 100, b: 0.9, D: 0.01 };
          return;
        }
        
        // Automatically fit best parameters
        params[phase] = fitDeclineParameters(series);
      });
    }
    return params;
  }, [phaseData, localHasData]);

  // 7) State for interactive parameters, chart options
  const [phaseParams, setPhaseParams] = useState(initialParams);
  const [colors, setColors] = useState({
    oil: "#008000",
    water: "#0000ff",
    gas: "#ff0000",
    pressure: "#000000"
  });
  const [activeKey, setActiveKey] = useState(null);
  const [yScaleType, setYScaleType] = useState("linear");
  const [yMultiplier, setYMultiplier] = useState(1.1);
  const [forecastDays, setForecastDays] = useState(90);

  // Update parameters when initialParams change (due to data update)
  useEffect(() => {
    if (autoForecast) {
      setPhaseParams(initialParams);
    }
  }, [initialParams, autoForecast]);

  // 8) Hotkey listeners (D, Q, B)
  useEffect(() => {
    const handleKeyDown = (e) => {
      const key = e.key.toLowerCase();
      if (["d", "q", "b"].includes(key)) {
        setActiveKey(key);
      }
    };
    const handleKeyUp = () => {
      setActiveKey(null);
    };
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  // 9) Build data arrays for historical & forecast
  const allHistoricalPoints = useMemo(() => {
    const points = [];
    Object.keys(phaseData).forEach(phase => {
      // historical
      phaseData[phase].forEach(d => {
        points.push({
          date: new Date(firstDate.getTime() + d.t * 24 * 60 * 60 * 1000),
          Q: d.value,
          phase
        });
      });
    });
    return points;
  }, [phaseData, firstDate]);

  // Calculate forecast for each phase
  const allForecastPoints = useMemo(() => {
    const forecasts = {};
    
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      
      // Skip if no valid parameters
      if (!Qi || D <= 0) {
        forecasts[phase] = [];
        return;
      }
      
      // Calculate Q at tMax as the "starting" forecast value
      const Q_start = calculateRate(Qi, b, D, tMax);
      const forecastStartDate = maxProdDate;
      const forecastData = [];
      const numForecastPts = 50;

      for (let i = 0; i <= numForecastPts; i++) {
        // tForecast is additional time beyond tMax
        const tForecast = (forecastDays * i) / numForecastPts;
        // forecast production using Arps equation
        const Q_forecast = calculateRate(Q_start, b, D, tForecast);
        // date is last prod date + tForecast
        const date = new Date(forecastStartDate.getTime() + tForecast * 24 * 60 * 60 * 1000);

        forecastData.push({
          date,
          Q: Q_forecast,
          phase
        });
      }
      forecasts[phase] = forecastData;
    });
    
    return forecasts;
  }, [phaseParams, tMax, maxProdDate, forecastDays]);

  // Flatten forecasts for domain calculations
  const combinedForecast = useMemo(() => {
    return Object.values(allForecastPoints).flat();
  }, [allForecastPoints]);
  
  const combinedAll = useMemo(() => {
    return [...allHistoricalPoints, ...combinedForecast];
  }, [allHistoricalPoints, combinedForecast]);

  // 10) Render the chart
  useEffect(() => {
    if (!localHasData) return;

    const margin = { top: 20, right: 30, bottom: 40, left: 60 };
    const width = 800;
    const height = 400;
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    // 11) Determine domain from combined historical + forecast data
    const [minDate, maxDate] = d3.extent(combinedAll, d => d.date);
    const [minQ, maxQ] = d3.extent(combinedAll, d => d.Q);

    // For safety in log scale, if minQ <= 0, we clamp to something > 0
    const minYVal = minQ <= 0 ? 1e-2 : minQ;

    // Build xScale
    const xScale = d3.scaleTime()
      .domain([minDate, maxDate])
      .range([margin.left, width - margin.right]);

    // Build yScale
    let yScale;
    if (yScaleType === "log") {
      yScale = d3.scaleLog()
        .domain([Math.max(1e-5, minYVal), maxQ * yMultiplier])
        .range([height - margin.bottom, margin.top]);
    } else {
      yScale = d3.scaleLinear()
        .domain([0, maxQ * yMultiplier])
        .range([height - margin.bottom, margin.top]);
    }

    // 12) Axes
    const xAxis = d3.axisBottom(xScale)
      .tickFormat(d3.timeFormat("%Y-%m-%d"));
    const yAxis = (yScaleType === "log")
      ? d3.axisLeft(yScale).ticks(6, "~s")
      : d3.axisLeft(yScale);

    // Add X and Y axes
    svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis)
      .selectAll("text")
      .attr("transform", "rotate(-45)")
      .style("text-anchor", "end");

    svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);

    // Add axis labels
    svg.append("text")
      .attr("x", width / 2)
      .attr("y", height - 5)
      .style("text-anchor", "middle")
      .text("Date");

    svg.append("text")
      .attr("transform", "rotate(-90)")
      .attr("x", -height / 2)
      .attr("y", 15)
      .style("text-anchor", "middle")
      .text("Production Rate");

    // 13) Plot historical raw data (circles)
    Object.keys(phasesDetected).forEach(phase => {
      const points = allHistoricalPoints.filter(p => p.phase === phase);
      svg.selectAll(`circle.hist-${phase}`)
        .data(points)
        .enter()
        .append("circle")
        .attr("class", `hist-${phase}`)
        .attr("cx", d => xScale(d.date))
        .attr("cy", d => yScale(d.Q))
        .attr("r", 3)
        .attr("fill", colors[phase] || "#888");
    });

    // 14) Plot historical decline lines with Arps model
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];

      // Build line data for "historical decline" (0 -> tMax)
      const histLineData = [];
      const numLinePts = 100;
      for (let i = 0; i <= numLinePts; i++) {
        const frac = i / numLinePts;
        const tVal = tMax * frac; // between 0 and tMax
        const QVal = calculateRate(Qi, b, D, tVal);
        const dateVal = new Date(firstDate.getTime() + tVal * 24 * 60 * 60 * 1000);
        histLineData.push({ date: dateVal, Q: QVal });
      }

      const lineGenHist = d3.line()
        .x(d => xScale(d.date))
        .y(d => yScale(d.Q))
        .curve(d3.curveMonotoneX);

      svg.append("path")
        .datum(histLineData)
        .attr("fill", "none")
        .attr("stroke", colors[phase] || "#888888")
        .attr("stroke-width", 2)
        .style("cursor", "pointer")
        .attr("d", lineGenHist)
        // DRAG: update D, Qi, or b based on pressed key
        .call(d3.drag()
          .on("drag", event => {
            setPhaseParams(prevParams => {
              const newParams = { ...prevParams };
              const currParams = { ...prevParams[phase] };
              
              if (activeKey === "d") {
                currParams.D = Math.max(0.0001, currParams.D + event.dy * 0.001);
              } else if (activeKey === "q") {
                currParams.Qi = Math.max(1, currParams.Qi - event.dy);
              } else if (activeKey === "b") {
                currParams.b = Math.max(0, Math.min(1, currParams.b + event.dx * 0.005));
              }
              
              newParams[phase] = currParams;
              return newParams;
            });
          })
        );
    });

    // 15) Plot forecast lines
    Object.keys(allForecastPoints).forEach(phase => {
      const fData = allForecastPoints[phase];
      if (!fData || !fData.length) return;
      
      const lineGenForecast = d3.line()
        .x(d => xScale(d.date))
        .y(d => yScale(d.Q))
        .curve(d3.curveMonotoneX);

      svg.append("path")
        .datum(fData)
        .attr("fill", "none")
        .attr("stroke", lightenColor(colors[phase] || "#888", 1.2))
        .attr("stroke-width", 3)
        .attr("stroke-dasharray", "5,3")
        .attr("d", lineGenForecast);
    });

    // Add a vertical line at the forecasting transition point
    svg.append("line")
      .attr("x1", xScale(maxProdDate))
      .attr("y1", margin.top)
      .attr("x2", xScale(maxProdDate))
      .attr("y2", height - margin.bottom)
      .attr("stroke", "#666")
      .attr("stroke-width", 1)
      .attr("stroke-dasharray", "5,5");

    // Add a "Forecast Starts" label
    svg.append("text")
      .attr("x", xScale(maxProdDate) + 5)
      .attr("y", margin.top + 15)
      .attr("fill", "#666")
      .text("Forecast â†’");

  }, [
    localHasData, firstDate, tMax, maxProdDate, data,
    phasesDetected, phaseData, phaseParams,
    colors, activeKey,
    yScaleType, yMultiplier, forecastDays,
    allHistoricalPoints, allForecastPoints, combinedAll
  ]);

  // Calculate estimated ultimate recovery for each phase
  const calculatedEUR = useMemo(() => {
    const results = {};
    
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      
      // For exponential decline (b = 0)
      if (b === 0) {
        results[phase] = Qi / D;
      }
      // For hyperbolic decline with b between 0 and 1
      else if (b > 0 && b < 1) {
        results[phase] = Qi / (D * (1 - b));
      }
      // For harmonic decline (b = 1)
      else if (b === 1) {
        // In this case, EUR is theoretically infinite
        // but we can calculate it to a practical economic limit
        const economicLimit = 1; // 1 unit of production as cutoff
        results[phase] = Qi * (1 + Math.log(Qi / economicLimit) / D);
      }
      else {
        results[phase] = "N/A"; // Invalid b value
      }
    });
    
    return results;
  }, [phaseParams]);

  if (!localHasData) {
    return <div>No valid data found.</div>;
  }

  return (
    <div className="dca-container">
      <h3>Interactive Decline Curve Analysis</h3>
      <svg ref={svgRef} width={800} height={400}></svg>

      {/* Display the parameters and EUR */}
      <div style={{ marginTop: '20px' }}>
        <h4>Decline Curve Parameters</h4>
        <table style={{ borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>Phase</th>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>Initial Rate (Qi)</th>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>Decline Exponent (b)</th>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>Decline Rate (D)</th>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left' }}>EUR</th>
            </tr>
          </thead>
          <tbody>
            {Object.keys(phaseParams).map(phase => (
              <tr key={phase}>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  <span style={{ 
                    display: 'inline-block', 
                    width: '12px', 
                    height: '12px', 
                    backgroundColor: colors[phase], 
                    marginRight: '5px' 
                  }}></span>
                  {phase.toUpperCase()}
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{phaseParams[phase].Qi.toFixed(2)}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{phaseParams[phase].b.toFixed(3)}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{phaseParams[phase].D.toFixed(4)}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  {typeof calculatedEUR[phase] === 'number' 
                    ? calculatedEUR[phase].toFixed(0) 
                    : calculatedEUR[phase]}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div style={{ marginTop: '15px' }}>
        <p>
          <strong>Interactive Controls:</strong><br />
          Hold <strong>D</strong> and drag vertically to adjust decline rate (D).<br />
          Hold <strong>Q</strong> and drag vertically to adjust initial rate (Qi).<br />
          Hold <strong>B</strong> and drag horizontally to adjust decline exponent (b).
        </p>
      </div>

      <div style={{ marginTop: '15px', display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        <div>
          <h4>Axis Controls:</h4>
          <div>
            <label>
              Y-Axis Scale Type:&nbsp;
              <label>
                <input
                  type="radio"
                  name="yScaleType"
                  value="linear"
                  checked={yScaleType === "linear"}
                  onChange={() => setYScaleType("linear")}
                /> Linear
              </label>
              &nbsp;
              <label>
                <input
                  type="radio"
                  name="yScaleType"
                  value="log"
                  checked={yScaleType === "log"}
                  onChange={() => setYScaleType("log")}
                /> Log
              </label>
            </label>
          </div>
          <div>
            <label>
              Y-Axis Multiplier: {yMultiplier.toFixed(2)}
              <input
                type="range"
                min="1"
                max="3"
                step="0.05"
                value={yMultiplier}
                onChange={(e) => setYMultiplier(+e.target.value)}
              />
            </label>
          </div>
          <div>
            <label>
              Forecast Extension (Days): {forecastDays}
              <input
                type="range"
                min="30"
                max="365"
                step="30"
                value={forecastDays}
                onChange={(e) => setForecastDays(+e.target.value)}
              />
            </label>
          </div>
        </div>

        <div>
          <h4>Phase Colors:</h4>
          {Object.keys(colors).map(phase => (
            <div key={phase} style={{ marginBottom: '5px' }}>
              <label>
                {phase.toUpperCase()} Color:&nbsp;
                <input
                  type="color"
                  value={colors[phase]}
                  onChange={(e) => setColors(prev => ({
                    ...prev,
                    [phase]: e.target.value
                  }))}
                />
              </label>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default InteractiveDCAChart;
