import React, { useRef, useEffect, useState, useMemo } from 'react';
import * as d3 from 'd3';

const darkenColor = (hex, factor = 0.8) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.floor(c.r * factor);
    c.g = Math.floor(c.g * factor);
    c.b = Math.floor(c.b * factor);
    return c.formatHex();
  }
  return hex;
};

const lightenColor = (hex, factor = 1.2) => {
  let c = d3.color(hex);
  if (c) {
    c.r = Math.min(255, Math.floor(c.r * factor));
    c.g = Math.min(255, Math.floor(c.g * factor));
    c.b = Math.min(255, Math.floor(c.b * factor));
    return c.formatHex();
  }
  return hex;
};

const detectDateColumn = (headers) => {
  const candidates = headers.filter(h => {
    const lower = h.toLowerCase();
    return (
      (lower.includes("prod") && lower.includes("date")) ||
      lower.includes("proddt") ||
      lower.includes("proddttm") ||
      lower === "date" ||
      lower === "datetime"
    );
  });
  return candidates.length > 0 ? candidates[0] : "Production_Date";
};

const detectColumns = (headers) => {
  let phases = {};
  headers.forEach(header => {
    const lower = header.toLowerCase();
    if (lower.includes("bopd")) {
      phases.oil = header;
    } else if (lower.includes("oil") && !phases.oil) {
      phases.oil = header;
    }
    if (lower.includes("bwpd")) {
      phases.water = header;
    } else if (lower.includes("water") && !phases.water) {
      phases.water = header;
    }
    if (lower.includes("mcfd")) {
      phases.gas = header;
    } else if (lower.includes("gas") && !phases.gas) {
      phases.gas = header;
    }
    if ((lower.includes("pip") || lower.includes("pressure") || lower.includes("psi")) && !phases.pressure) {
      phases.pressure = header;
    }
  });
  return phases;
};

const calculateRate = (Qi, b, D, t) => {
  if (b === 0) {
    return Qi * Math.exp(-D * t);
  } else {
    return Qi / Math.pow(1 + b * D * t, 1 / b);
  }
};

const fitDeclineParameters = (data, fitWindow = 60) => {
  if (!data || data.length < 3) return { Qi: data?.[0]?.value || 100, b: 0.9, D: 0.01 };
  const sortedData = [...data].sort((a, b) => a.t - b.t);
  const Qi = sortedData[0].value;
  const recentData = sortedData.slice(-Math.min(fitWindow, sortedData.length));
  let bestB = 0, bestD = 0.01, bestError = Infinity;
  for (let b = 0; b <= 1; b += 0.1) {
    const { D, error } = findOptimalD(recentData, Qi, b);
    if (error < bestError) {
      bestError = error;
      bestB = b;
      bestD = D;
    }
  }
  const minB = Math.max(0, bestB - 0.1);
  const maxB = Math.min(1, bestB + 0.1);
  for (let b = minB; b <= maxB; b += 0.01) {
    const { D, error } = findOptimalD(recentData, Qi, b);
    if (error < bestError) {
      bestError = error;
      bestB = b;
      bestD = D;
    }
  }
  return { Qi, b: bestB, D: bestD };
};

const findOptimalD = (data, Qi, b) => {
  let bestD = 0.01, bestError = Infinity;
  for (let D = 0.001; D <= 0.5; D += 0.005) {
    let error = 0;
    data.forEach(d => {
      const predicted = calculateRate(Qi, b, D, d.t);
      error += Math.pow(predicted - d.value, 2);
    });
    if (error < bestError) {
      bestError = error;
      bestD = D;
    }
  }
  const minD = Math.max(0.001, bestD - 0.005);
  const maxD = bestD + 0.005;
  for (let D = minD; D <= maxD; D += 0.0005) {
    let error = 0;
    data.forEach(d => {
      const predicted = calculateRate(Qi, b, D, d.t);
      error += Math.pow(predicted - d.value, 2);
    });
    if (error < bestError) {
      bestError = error;
      bestD = D;
    }
  }
  return { D: bestD, error: bestError };
};

function InteractiveDCAChart({
  dataString,
  startDate,
  endDate,
  colors,
  yScaleType,
  yMultiplier, // Always 1 from parent.
  forecastDays,
  autoForecast = true,
  onParametersCalculated
}) {
  const svgRef = useRef(null);
  const xScaleRef = useRef(null);
  // For tooltip: store hover info (declared only once).
  const [hoverInfo, setHoverInfo] = useState(null);

  // Parse CSV data.
  const parsedData = useMemo(() => {
    try {
      return d3.csvParse(dataString);
    } catch (err) {
      console.error("Error parsing CSV:", err);
      return [];
    }
  }, [dataString]);

  const hasData = parsedData.length > 0;
  const prodDateCol = hasData ? detectDateColumn(parsedData.columns) : "Production_Date";
  const phasesDetected = hasData ? detectColumns(parsedData.columns) : {};
  const filteredData = useMemo(() => {
    if (!hasData) return [];
    let dataWithDates = parsedData.map(row => ({
      ...row,
      [prodDateCol]: new Date(row[prodDateCol])
    }));
    dataWithDates = dataWithDates.filter(row => !isNaN(row[prodDateCol].getTime()));
    dataWithDates.sort((a, b) => a[prodDateCol] - b[prodDateCol]);
    if (startDate) {
      const sd = new Date(startDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] >= sd);
    }
    if (endDate) {
      const ed = new Date(endDate);
      dataWithDates = dataWithDates.filter(row => row[prodDateCol] <= ed);
    }
    return dataWithDates;
  }, [parsedData, prodDateCol, startDate, endDate, hasData]);

  const localHasData = filteredData.length > 0;
  const firstDate = localHasData ? filteredData[0][prodDateCol] : new Date();

  const data = useMemo(() => {
    if (!localHasData) return [];
    return filteredData.map(row => {
      const date = row[prodDateCol];
      return { ...row, t: (date - firstDate) / (1000 * 60 * 60 * 24) };
    });
  }, [filteredData, localHasData, firstDate, prodDateCol]);

  const phaseData = useMemo(() => {
    let result = {};
    Object.keys(phasesDetected).forEach(phase => {
      result[phase] = data.map(d => ({
        t: d.t,
        value: +d[phasesDetected[phase]]
      })).filter(d => !isNaN(d.value));
    });
    return result;
  }, [data, phasesDetected]);

  const tMax = localHasData ? d3.max(data, d => d.t) : 0;
  const maxProdDate = localHasData
    ? d3.max(data, d => d[prodDateCol])
    : new Date();

  const [phaseParams, setPhaseParams] = useState({});
  const [autoFitEnabled, setAutoFitEnabled] = useState(true);
  const [activeKey, setActiveKey] = useState(null);

  // Handle key events.
  useEffect(() => {
    const handleKeyDown = (e) => {
      const key = e.key.toLowerCase();
      if (["d", "q", "b"].includes(key)) setActiveKey(key);
    };
    const handleKeyUp = () => setActiveKey(null);
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  // Auto-fit decline parameters.
  // Use a ref to hold previous parameters and only update when they truly change.
  const prevParamsRef = useRef({});
  useEffect(() => {
    let newParams = {};
    Object.keys(phaseData).forEach(phase => {
      let series = phaseData[phase];
      if (series.length < 3) {
        newParams[phase] = { Qi: series?.[0]?.value || 100, b: 0.9, D: 0.01 };
      } else {
        newParams[phase] = fitDeclineParameters(series);
      }
    });
    if (autoFitEnabled && JSON.stringify(newParams) !== JSON.stringify(prevParamsRef.current)) {
      prevParamsRef.current = newParams;
      setPhaseParams(newParams);
    }
  }, [phaseData, autoFitEnabled]);

  const calculatedEUR = useMemo(() => {
    let results = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      if (b === 0) results[phase] = Qi / D;
      else if (b > 0 && b < 1) results[phase] = Qi / (D * (1 - b));
      else if (b === 1) results[phase] = Qi * (1 + Math.log(Qi / 1) / D);
      else results[phase] = "N/A";
    });
    return results;
  }, [phaseParams]);

  // Compute forecast average for first 60 days.
  const forecastAverage = useMemo(() => {
    let fa = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      const Q_start = calculateRate(Qi, b, D, tMax);
      const steps = 60;
      let sum = 0;
      for (let i = 0; i <= steps; i++) {
        const t = (i / steps) * 60;
        const Q = b === 0 ? Q_start * Math.exp(-D * t) : Q_start / Math.pow(1 + b * D * t, 1 / b);
        sum += Q;
      }
      fa[phase] = sum / (steps + 1);
    });
    return fa;
  }, [phaseParams, tMax]);

  useEffect(() => {
    if (onParametersCalculated) {
      onParametersCalculated({ phaseParams, calculatedEUR, forecastAverage });
    }
  }, [phaseParams, calculatedEUR, forecastAverage, onParametersCalculated]);

  const allHistoricalPoints = useMemo(() => {
    let points = [];
    Object.keys(phaseData).forEach(phase => {
      phaseData[phase].forEach(d => {
        points.push({
          date: new Date(firstDate.getTime() + d.t * 24 * 60 * 60 * 1000),
          Q: d.value,
          phase
        });
      });
    });
    return points;
  }, [phaseData, firstDate]);

  const calcForecast = (Qi, b, D, tMaxVal, forecastDaysVal) => {
    let Qstart = calculateRate(Qi, b, D, tMaxVal);
    let arr = [];
    let steps = 50;
    let lastDate = localHasData ? d3.max(data, d => d[prodDateCol]) : new Date();
    for (let i = 0; i <= steps; i++) {
      let tF = (forecastDaysVal * i) / steps;
      let q = calculateRate(Qstart, b, D, tF);
      let dt = new Date(lastDate.getTime() + tF * 24 * 60 * 60 * 1000);
      arr.push({ date: dt, Q: q, t: tF, phase: "forecast" });
    }
    return arr;
  };

  const allForecastPoints = useMemo(() => {
    let forecasts = {};
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      if (!Qi || D <= 0) {
        forecasts[phase] = [];
      } else {
        let fc = calcForecast(Qi, b, D, tMax, forecastDays);
        fc.forEach(x => (x.phase = phase));
        forecasts[phase] = fc;
      }
    });
    return forecasts;
  }, [phaseParams, tMax, forecastDays, localHasData, data, prodDateCol]);

  const combinedAll = useMemo(() => {
    let all = [...allHistoricalPoints];
    Object.values(allForecastPoints).forEach(arr => (all = all.concat(arr)));
    return all;
  }, [allHistoricalPoints, allForecastPoints]);

  // D3 rendering.
  useEffect(() => {
    if (!localHasData) return;
    const margin = { top: 20, right: 30, bottom: 40, left: 60 };
    const width = 800;
    const height = 400;
    const svg = d3.select(svgRef.current);
    svg.selectAll("*").remove();

    const [minDateDomain, maxDateDomain] = d3.extent(combinedAll, d => d.date);
    let [minQ, maxQ] = d3.extent(combinedAll, d => d.Q);
    if (yScaleType === "log" && minQ <= 0) minQ = 1e-2;
    const xScale = d3.scaleTime()
      .domain([minDateDomain, maxDateDomain])
      .range([margin.left, width - margin.right]);
    xScaleRef.current = xScale;
    const yDomain = yScaleType === "log"
      ? [Math.max(1e-5, minQ), maxQ]
      : [0, maxQ];
    let yScale = yScaleType === "log"
      ? d3.scaleLog().domain(yDomain).range([height - margin.bottom, margin.top])
      : d3.scaleLinear().domain(yDomain).range([height - margin.bottom, margin.top]);
    const xAxis = d3.axisBottom(xScale).tickFormat(d3.timeFormat("%Y-%m-%d"));
    const yAxis = yScaleType === "log"
      ? d3.axisLeft(yScale).ticks(6, "~s")
      : d3.axisLeft(yScale);
    svg.append("g")
      .attr("transform", `translate(0,${height - margin.bottom})`)
      .call(xAxis)
      .selectAll("text")
      .attr("transform", "rotate(-45)")
      .style("text-anchor", "end");
    svg.append("g")
      .attr("transform", `translate(${margin.left},0)`)
      .call(yAxis);
    svg.append("text")
      .attr("x", width / 2)
      .attr("y", height - 5)
      .style("text-anchor", "middle")
      .text("Date");
    svg.append("text")
      .attr("transform", "rotate(-90)")
      .attr("x", -height / 2)
      .attr("y", 15)
      .style("text-anchor", "middle")
      .text("Production Rate");
    
    // Plot historical points.
    Object.keys(phasesDetected).forEach(phase => {
      const pts = allHistoricalPoints.filter(p => p.phase === phase);
      svg.selectAll(`circle.hist-${phase}`)
        .data(pts)
        .enter()
        .append("circle")
        .attr("cx", d => xScale(d.date))
        .attr("cy", d => yScale(d.Q))
        .attr("r", 3)
        .attr("fill", colors[phase] || "#888");
    });
    
    // Plot decline lines with drag behavior.
    Object.keys(phaseParams).forEach(phase => {
      const { Qi, b, D } = phaseParams[phase];
      const lineData = [];
      const steps = 100;
      for (let i = 0; i <= steps; i++) {
        const frac = i / steps;
        const tVal = tMax * frac;
        const QVal = calculateRate(Qi, b, D, tVal);
        const dt = new Date(firstDate.getTime() + tVal * 24 * 60 * 60 * 1000);
        lineData.push({ date: dt, Q: QVal });
      }
      const lineGen = d3.line()
        .x(d => xScale(d.date))
        .y(d => yScale(d.Q))
        .curve(d3.curveMonotoneX);
      svg.append("path")
        .datum(lineData)
        .attr("fill", "none")
        .attr("stroke", colors[phase] || "#888888")
        .attr("stroke-width", 2)
        .style("cursor", "pointer")
        .attr("d", lineGen)
        .call(
          d3.drag().on("drag", (event) => {
            if (autoFitEnabled) setAutoFitEnabled(false);
            setPhaseParams(prev => {
              const newParams = { ...prev };
              const curr = { ...prev[phase] };
              if (activeKey === "d") {
                curr.D = Math.max(0.0001, curr.D + event.dy * 0.001);
              } else if (activeKey === "q") {
                curr.Qi = Math.max(1, curr.Qi - event.dy);
              } else if (activeKey === "b") {
                curr.b = Math.max(0, Math.min(1, curr.b + event.dy * 0.005));
              }
              newParams[phase] = curr;
              return newParams;
            });
          })
        );
    });
    
    // Plot forecast lines with hover and grab effects.
    Object.keys(allForecastPoints).forEach(phase => {
      const fc = allForecastPoints[phase];
      if (fc.length > 1) {
        const lineGenFc = d3.line()
          .x(d => xScale(d.date))
          .y(d => yScale(d.Q))
          .curve(d3.curveMonotoneX);
        svg.append("path")
          .datum(fc)
          .attr("fill", "none")
          .attr("stroke", d3.color(colors[phase] || "#888").brighter(1.2))
          .attr("stroke-width", 3)
          .attr("stroke-dasharray", "5,3")
          .attr("d", lineGenFc)
          .on("mouseover", function() {
            d3.select(this).attr("stroke-width", 5);
          })
          .on("mouseout", function() {
            d3.select(this).attr("stroke-width", 3);
          })
          .on("mousedown", function() {
            d3.select(this).style("cursor", "grabbing");
          })
          .on("mouseup", function() {
            d3.select(this).style("cursor", "grab");
          });
      }
    });
    
    svg.append("line")
      .attr("x1", xScale(maxProdDate))
      .attr("y1", margin.top)
      .attr("x2", xScale(maxProdDate))
      .attr("y2", height - margin.bottom)
      .attr("stroke", "#666")
      .attr("stroke-width", 1)
      .attr("stroke-dasharray", "5,5");
    svg.append("text")
      .attr("x", xScale(maxProdDate) + 5)
      .attr("y", margin.top + 15)
      .attr("fill", "#666")
      .text("Forecast →");
    
    // d3-zoom for vertical scaling.
    const zoomBehavior = d3.zoom()
      .scaleExtent([0.5, 10])
      .on("zoom", (event) => {
        // Only update DOM elements, no state updates.
        const newYScale = event.transform.rescaleY(yScale);
        svg.selectAll("circle").attr("cy", d => newYScale(d.Q));
        svg.selectAll("path").each(function(d) {
          // If d is an array of data points (lines), update them.
          if (Array.isArray(d)) {
            const newLineGen = d3.line()
              .x(dd => xScale(dd.date))
              .y(dd => newYScale(dd.Q))
              .curve(d3.curveMonotoneX);
            d3.select(this).attr("d", newLineGen(d));
          }
        });
        svg.select("g").call(yAxis.scale(newYScale));
      });
    svg.call(zoomBehavior);
  }, [
    combinedAll,
    colors,
    activeKey,
    yScaleType,
    forecastDays,
    autoFitEnabled,
    tMax,
    firstDate,
    phasesDetected,
    phaseParams,
    allHistoricalPoints,
    allForecastPoints,
    maxProdDate
  ]);

  return (
    <div style={{ position: 'relative' }}>
      <svg
        ref={svgRef}
        width={800}
        height={400}
        onMouseMove={(event) => {
          if (!localHasData || !xScaleRef.current) return;
          const [mouseX] = d3.pointer(event, svgRef.current);
          const date = xScaleRef.current.invert(mouseX);
          const t = (date - firstDate) / (1000 * 60 * 60 * 24);
          let values = {};
          Object.keys(phaseParams).forEach(phase => {
            const { Qi, b, D } = phaseParams[phase] || {};
            if (Qi && b !== undefined && D) {
              values[phase] = calculateRate(Qi, b, D, t);
            }
          });
          setHoverInfo({ x: mouseX, date, values });
        }}
        onMouseOut={() => setHoverInfo(null)}
      />
      {hoverInfo && (
        <div
          style={{
            position: 'absolute',
            left: hoverInfo.x + 5,
            top: 0,
            pointerEvents: 'none',
            background: 'rgba(255,255,255,0.9)',
            border: '1px solid #ccc',
            padding: 5,
            fontSize: 12,
            borderRadius: 4
          }}
        >
          <div>{hoverInfo.date.toDateString()}</div>
          {Object.keys(hoverInfo.values).map(phase => (
            <div key={phase}>
              {phase.toUpperCase()}: {hoverInfo.values[phase].toFixed(2)}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default InteractiveDCAChart;
